import React, { useState, useEffect } from 'react';
import { Drawer, Select, Spin, message, Typography, Space, Button } from 'antd';
import { CloseOutlined, ReloadOutlined } from '@ant-design/icons';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { apiService, HistoryData } from '../services/api';
import { formatPrice, formatPercent } from '../utils/helpers';

const { Title, Text } = Typography;
const { Option } = Select;

interface StockChartProps {
  visible: boolean;
  onClose: () => void;
  stockCode: string;
  stockName: string;
}

interface ChartData {
  date: string;
  close: number;
  formattedDate: string;
}

const StockChart: React.FC<StockChartProps> = ({ visible, onClose, stockCode, stockName }) => {
  const [loading, setLoading] = useState(false);
  const [chartData, setChartData] = useState<ChartData[]>([]);
  const [period, setPeriod] = useState('1m');
  const [priceChange, setPriceChange] = useState<{ amount: number; percent: number } | null>(null);

  // 时间周期选项
  const periodOptions = [
    { value: '1w', label: '1周' },
    { value: '1m', label: '1月' },
    { value: '3m', label: '3月' },
    { value: '6m', label: '6月' },
    { value: '1y', label: '1年' },
  ];

  // 获取股票历史数据
  const fetchStockHistory = async (code: string, selectedPeriod: string) => {
    if (!code) return;

    setLoading(true);
    try {
      const response = await apiService.getStockHistory(code, selectedPeriod);
      if (response.success && response.data) {
        // 转换数据格式
        const formattedData: ChartData[] = response.data.map((item: HistoryData) => ({
          date: item.date,
          close: item.close,
          formattedDate: formatDate(item.date),
        }));

        // 按日期排序
        formattedData.sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime());

        setChartData(formattedData);

        // 计算价格变化
        if (formattedData.length >= 2) {
          const firstPrice = formattedData[0].close;
          const lastPrice = formattedData[formattedData.length - 1].close;
          const changeAmount = lastPrice - firstPrice;
          const changePercent = (changeAmount / firstPrice) * 100;
          setPriceChange({ amount: changeAmount, percent: changePercent });
        }
      } else {
        message.error(response.message || '获取历史数据失败');
        setChartData([]);
        setPriceChange(null);
      }
    } catch (error) {
      console.error('获取历史数据失败:', error);
      message.error('获取历史数据失败，请重试');
      setChartData([]);
      setPriceChange(null);
    } finally {
      setLoading(false);
    }
  };

  // 格式化日期显示
  const formatDate = (dateStr: string) => {
    const date = new Date(dateStr);
    const month = date.getMonth() + 1;
    const day = date.getDate();
    return `${month}/${day}`;
  };

  // 自定义Tooltip
  const CustomTooltip = ({ active, payload, label }: any) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      return (
        <div style={{
          backgroundColor: '#1f2937',
          border: '1px solid #374151',
          borderRadius: '8px',
          padding: '12px',
          boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
        }}>
          <p style={{ color: '#e5e7eb', margin: '0 0 4px 0', fontSize: '14px' }}>
            日期: {data.date}
          </p>
          <p style={{ color: '#60a5fa', margin: 0, fontSize: '14px', fontWeight: 600 }}>
            收盘价: ¥{formatPrice(data.close)}
          </p>
        </div>
      );
    }
    return null;
  };

  // 当抽屉打开或股票代码/周期改变时获取数据
  useEffect(() => {
    if (visible && stockCode) {
      fetchStockHistory(stockCode, period);
    }
  }, [visible, stockCode, period]);

  // 处理周期变化
  const handlePeriodChange = (newPeriod: string) => {
    setPeriod(newPeriod);
  };

  // 刷新数据
  const handleRefresh = () => {
    fetchStockHistory(stockCode, period);
  };

  return (
    <Drawer
      title={null}
      placement="right"
      onClose={onClose}
      open={visible}
      width="100%"
      style={{ backgroundColor: '#111827' }}
      bodyStyle={{ 
        padding: 0, 
        backgroundColor: '#111827',
        height: '100vh',
        overflow: 'hidden'
      }}
      headerStyle={{ display: 'none' }}
      closable={false}
    >
      <div style={{ 
        height: '100vh', 
        display: 'flex', 
        flexDirection: 'column',
        backgroundColor: '#111827'
      }}>
        {/* 头部 */}
        <div style={{
          padding: '24px 32px',
          borderBottom: '1px solid #2d3748',
          backgroundColor: '#1a202c',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.15)'
        }}>
          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'flex-start',
            minHeight: '64px'
          }}>
            {/* 左侧股票信息 */}
            <div style={{ flex: 1 }}>
              <div style={{ 
                display: 'flex', 
                alignItems: 'baseline', 
                marginBottom: '12px',
                gap: '16px'
              }}>
                <Title level={2} style={{ 
                  color: '#ffffff', 
                  margin: 0,
                  fontSize: '24px',
                  fontWeight: 600,
                  lineHeight: 1.2
                }}>
                  {stockName}
                </Title>
                <Text style={{ 
                  color: '#a0aec0', 
                  fontSize: '16px',
                  fontWeight: 500
                }}>
                  {stockCode}
                </Text>
              </div>

              {priceChange && (
                <div style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  gap: '16px',
                  flexWrap: 'wrap'
                }}>
                  <div style={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: '8px',
                    padding: '6px 12px',
                    borderRadius: '8px',
                    backgroundColor: priceChange.amount >= 0 ? 'rgba(34, 197, 94, 0.1)' : 'rgba(239, 68, 68, 0.1)',
                    border: `1px solid ${priceChange.amount >= 0 ? 'rgba(34, 197, 94, 0.2)' : 'rgba(239, 68, 68, 0.2)'}`
                  }}>
                    <Text style={{ 
                      color: priceChange.amount >= 0 ? '#22c55e' : '#ef4444',
                      fontSize: '18px',
                      fontWeight: 700
                    }}>
                      {priceChange.amount >= 0 ? '+' : ''}{formatPrice(priceChange.amount)}
                    </Text>
                    <Text style={{ 
                      color: priceChange.amount >= 0 ? '#22c55e' : '#ef4444',
                      fontSize: '16px',
                      fontWeight: 600
                    }}>
                      ({priceChange.amount >= 0 ? '+' : ''}{formatPercent(priceChange.percent)})
                    </Text>
                  </div>

                  <div style={{
                    padding: '6px 12px',
                    borderRadius: '6px',
                    backgroundColor: '#2d3748',
                    border: '1px solid #4a5568'
                  }}>
                    <Text style={{ 
                      color: '#e2e8f0', 
                      fontSize: '14px',
                      fontWeight: 500
                    }}>
                      {periodOptions.find(opt => opt.value === period)?.label}周期
                    </Text>
                  </div>
                </div>
              )}
            </div>

            {/* 右侧操作按钮 */}
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '12px',
              marginLeft: '24px'
            }}>
              <Select
                value={period}
                onChange={handlePeriodChange}
                style={{ 
                  width: 120,
                  height: '40px'
                }}
                size="large"
                dropdownStyle={{
                  backgroundColor: '#2d3748',
                  border: '1px solid #4a5568'
                }}
              >
                {periodOptions.map(option => (
                  <Option key={option.value} value={option.value}>
                    {option.label}
                  </Option>
                ))}
              </Select>

              <Button
                icon={<ReloadOutlined />}
                onClick={handleRefresh}
                loading={loading}
                type="default"
                size="large"
                style={{
                  height: '40px',
                  borderColor: '#4a5568',
                  backgroundColor: '#2d3748',
                  color: '#e2e8f0'
                }}
              >
                刷新
              </Button>

              <Button
                icon={<CloseOutlined />}
                onClick={onClose}
                type="text"
                size="large"
                style={{ 
                  height: '40px',
                  color: '#a0aec0',
                  backgroundColor: 'transparent'
                }}
              >
                关闭
              </Button>
            </div>
          </div>
        </div>

        {/* 图表区域 */}
        <div style={{ 
          flex: 1, 
          padding: '24px',
          backgroundColor: '#111827',
          overflow: 'hidden'
        }}>
          {loading ? (
            <div style={{ 
              display: 'flex', 
              justifyContent: 'center', 
              alignItems: 'center', 
              height: '100%' 
            }}>
              <Spin size="large" />
            </div>
          ) : chartData.length > 0 ? (
            <ResponsiveContainer width="100%" height="100%">
              <LineChart
                data={chartData}
                margin={{ top: 20, right: 30, left: 20, bottom: 20 }}
              >
                <CartesianGrid 
                  strokeDasharray="3 3" 
                  stroke="#374151" 
                  opacity={0.3}
                />
                <XAxis 
                  dataKey="formattedDate"
                  stroke="#9ca3af"
                  fontSize={12}
                  tickLine={false}
                  axisLine={{ stroke: '#374151' }}
                />
                <YAxis 
                  stroke="#9ca3af"
                  fontSize={12}
                  tickLine={false}
                  axisLine={{ stroke: '#374151' }}
                  tickFormatter={(value) => `¥${formatPrice(value)}`}
                  domain={['dataMin - 1', 'dataMax + 1']}
                />
                <Tooltip content={<CustomTooltip />} />
                <Line 
                  type="monotone" 
                  dataKey="close" 
                  stroke="#60a5fa"
                  strokeWidth={2}
                  dot={false}
                  activeDot={{ 
                    r: 4, 
                    fill: '#60a5fa',
                    stroke: '#ffffff',
                    strokeWidth: 2
                  }}
                />
              </LineChart>
            </ResponsiveContainer>
          ) : (
            <div style={{ 
              display: 'flex', 
              justifyContent: 'center', 
              alignItems: 'center', 
              height: '100%',
              flexDirection: 'column',
              color: '#9ca3af'
            }}>
              <Text style={{ color: '#9ca3af', fontSize: '16px' }}>
                暂无数据
              </Text>
              <Button 
                type="primary" 
                onClick={handleRefresh}
                style={{ marginTop: '16px' }}
              >
                重新加载
              </Button>
            </div>
          )}
        </div>
      </div>
    </Drawer>
  );
};

export default StockChart;
