/**
 * 股票数据看板页面
 * 完整的股票数据展示和管理功能
 */

import React, { useState, useEffect, useCallback } from 'react';
import { Layout, message, Spin, Button, Space, Typography, Drawer } from 'antd';
import { ReloadOutlined, PlusOutlined, LogoutOutlined, ToolOutlined } from '@ant-design/icons';
import { StockInfo, apiService } from '../services/api';
import SearchBox from '../components/SearchBox';
import StatsCards from '../components/StatsCards';
import StockTable from '../components/StockTable';
import { NetworkDiagnostic, NetworkDiagnosticResult } from '../utils/networkDiagnostic';
import '../styles/DashboardPage.css';

const { Content } = Layout;
const { Title } = Typography;

interface DashboardPageProps {
  onLogout: () => void;
}

const DashboardPage: React.FC<DashboardPageProps> = ({ onLogout }) => {
  const [stocks, setStocks] = useState<StockInfo[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [refreshing, setRefreshing] = useState<boolean>(false);
  const [drawerOpen, setDrawerOpen] = useState<boolean>(false);
  const [diagnosticVisible, setDiagnosticVisible] = useState(false);
  const [diagnosticResults, setDiagnosticResults] = useState<NetworkDiagnosticResult[]>([]);
  const [diagnosticLoading, setDiagnosticLoading] = useState(false);

  // 获取关注列表
  const fetchWatchlist = useCallback(async (showLoading = true) => {
    if (showLoading) {
      setLoading(true);
    }

    console.log('=== DashboardPage 获取关注列表 ===');
    console.log('开始获取关注列表...');

    try {
      const response = await apiService.getWatchlist();
      console.log('关注列表响应:', response);

      if (response.success && response.data) {
        setStocks(response.data);
        console.log('关注列表更新成功，数量:', response.data.length);
      } else {
        const errorMsg = response.error || '获取关注列表失败';
        console.error('关注列表API返回失败:', errorMsg);
        message.error(errorMsg);
      }
    } catch (error: any) {
      console.error('获取关注列表异常:', error);

      let errorMessage = '获取关注列表失败，请重试';

      if (error.response) {
        // 服务器响应了错误状态码
        const status = error.response.status;
        const data = error.response.data;

        console.error('HTTP错误:', status, data);

        if (status === 401) {
          errorMessage = '认证失败，请重新登录';
        } else if (status === 403) {
          errorMessage = '权限不足';
        } else if (status === 500) {
          errorMessage = '服务器内部错误';
        } else if (data && data.message) {
          errorMessage = data.message;
        } else if (data && data.error) {
          errorMessage = data.error;
        }
      } else if (error.request) {
        // 请求已发出但没有收到响应
        console.error('网络错误详情:', {
          request: error.request,
          code: error.code,
          message: error.message,
          config: error.config
        });

        // 根据错误类型提供更具体的错误信息
        if (error.code === 'ECONNREFUSED') {
          errorMessage = '无法连接到服务器，请确认后端服务已启动';
        } else if (error.code === 'NETWORK_ERROR') {
          errorMessage = '网络连接错误，请检查网络设置';
        } else if (error.code === 'TIMEOUT') {
          errorMessage = '请求超时，请稍后重试';
        } else {
          errorMessage = '网络连接失败，请检查网络连接或稍后重试';
        }
      } else {
        // 其他错误
        console.error('其他错误:', error.message);
        errorMessage = error.message || errorMessage;
      }

      message.error(errorMessage);
    } finally {
      if (showLoading) {
        setLoading(false);
      }
    }
  }, []);

  // 刷新数据
  const handleRefresh = async () => {
    setRefreshing(true);
    try {
      await fetchWatchlist(false);
      message.success('数据已刷新');
    } catch (error) {
      message.error('刷新失败');
    } finally {
      setRefreshing(false);
    }
  };

  // 处理退出登录
  const handleLogout = () => {
    if (window.confirm('确定要退出登录吗？')) {
      onLogout();
    }
  };

  // 添加股票到关注列表
  const handleAddStock = async (stock: { code: string; name: string; industry?: string }) => {
    try {
      const response = await apiService.addToWatchlist(stock.code, stock.industry || '');
      if (response.success) {
        message.success(`已添加 ${stock.name} 到关注列表`);
        await fetchWatchlist(false); // 刷新列表
      } else {
        message.error(response.error || '添加失败');
      }
    } catch (error) {
      console.error('添加股票失败:', error);
      message.error('添加失败，请重试');
    }
  };

  // 从关注列表移除股票
  const handleRemoveStock = async (code: string) => {
    try {
      const response = await apiService.removeFromWatchlist(code);
      if (response.success) {
        message.success('已从关注列表移除');
        await fetchWatchlist(false); // 刷新列表
      } else {
        message.error(response.error || '移除失败');
      }
    } catch (error) {
      console.error('移除股票失败:', error);
      message.error('移除失败，请重试');
    }
  };

  // 处理抽屉状态变化
  const handleDrawerStateChange = (isOpen: boolean) => {
    setDrawerOpen(isOpen);
  };

  // 初始化加载数据
  useEffect(() => {
    fetchWatchlist();
  }, [fetchWatchlist]);

  return (
    <Content style={{ 
      padding: '0', /* 完全移除padding */
      margin: '0', /* 完全移除margin */
      minHeight: 'calc(100vh - 64px - 40px)', /* 减去header和footer高度 */
      background: '#1a1d29', /* 修复背景色为原来的深蓝灰色 */
      display: 'flex',
      flexDirection: 'column'
    }}>
      <div style={{ 
        maxWidth: '1400px', 
        margin: '0 auto',
        width: '100%',
        flex: 1,
        display: 'flex',
        flexDirection: 'column'
      }}>
        {/* 页面标题、搜索框和操作栏 - 使用flexbox布局水平对齐 */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center', 
          padding: '8px 24px',
          paddingTop: '8px',
          marginBottom: '16px'
        }}>
          {/* 左侧标题 */}
          <h1 style={{ 
            margin: 0, 
            fontSize: '24px', 
            fontWeight: 'bold', 
            color: '#ffffff',
            flexShrink: 0,
            width: '180px'
          }}>
            股票数据看板
          </h1>

          {/* 中间搜索框 */}
          <div style={{ 
            flex: 1,
            display: 'flex',
            justifyContent: 'center'
          }}>
            <SearchBox 
              onSelectStock={handleAddStock}
              placeholder="搜索股票代码或名称，点击添加到关注列表"
            />
          </div>

          {/* 右侧按钮组 */}
          <div style={{
            width: '280px',
            display: 'flex',
            justifyContent: 'flex-end',
            flexShrink: 0,
            gap: '8px'
          }}>
            <Button 
              type="primary" 
              icon={<ReloadOutlined />}
              onClick={handleRefresh}
              loading={refreshing}
              style={{
                background: '#667eea',
                borderColor: '#667eea',
                borderRadius: '6px'
              }}
            >
              刷新数据
            </Button>
            <Button 
              icon={<LogoutOutlined />}
              onClick={handleLogout}
              style={{
                background: '#ff4757',
                borderColor: '#ff4757',
                color: '#ffffff',
                borderRadius: '6px'
              }}
            >
              退出登录
            </Button>
          </div>
        </div>

        {/* 统计卡片 */}
        <div style={{ 
          marginBottom: '16px',
          padding: '0 24px'
        }}>
          <StatsCards stocks={stocks} loading={loading} />
        </div>

        {/* 股票列表 */}
        <div style={{ 
          background: 'rgba(255, 255, 255, 0.02)',
          borderRadius: '12px',
          padding: '20px',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          flex: 1,
          display: 'flex',
          flexDirection: 'column',
          margin: '0 24px 24px 24px' /* 左右和底部margin */
        }}>
          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            marginBottom: '16px'
          }}>
            <Title level={4} style={{ margin: 0, color: '#ffffff' }}>
              我的关注列表
            </Title>
            <span style={{ color: '#888', fontSize: '14px' }}>
              API调用: {loading ? '加载中...' : `共 ${stocks.length} 只股票`}
            </span>
          </div>

          <div style={{ flex: 1 }}>
            {loading ? (
              <div style={{ 
                display: 'flex', 
                justifyContent: 'center', 
                alignItems: 'center',
                height: '200px'
              }}>
                <Spin size="large" />
              </div>
            ) : (
              <StockTable 
                stocks={stocks}
                loading={refreshing}
                onRemoveStock={handleRemoveStock}
                onDrawerStateChange={handleDrawerStateChange}
                onRefresh={fetchWatchlist}
              />
            )}
          </div>
        </div>
      </div>
    </Content>
  );
};

export default DashboardPage;
