/**
 * 注册组件
 * 提供用户注册界面 - 使用内联样式确保样式生效
 */

import React, { useState } from 'react';
import { Form, Input, Button, Typography, message, Checkbox } from 'antd';
import { UserOutlined, LockOutlined, MailOutlined, EyeInvisibleOutlined, EyeTwoTone, WechatOutlined } from '@ant-design/icons';
import { authService } from '../../services/authService';

const { Title, Text } = Typography;

interface RegisterForm {
  username: string;
  email: string;
  password: string;
  confirmPassword: string;
  agreement: boolean;
}

interface RegisterProps {
  onRegisterSuccess?: () => void;
  onSwitchToLogin?: () => void;
}

// 内联样式定义
const styles = {
  container: {
    width: '100%',
    maxWidth: '400px',
    margin: '0 auto',
    padding: '0 20px',
  } as React.CSSProperties,

  header: {
    textAlign: 'center' as const,
    marginBottom: '32px',
  } as React.CSSProperties,

  title: {
    fontSize: '28px',
    fontWeight: 'bold',
    color: '#ffffff',
    marginBottom: '8px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  subtitle: {
    fontSize: '16px',
    color: 'rgba(255, 255, 255, 0.7)',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  formGroup: {
    marginBottom: '20px',
  } as React.CSSProperties,

  input: {
    width: '100%',
    height: '48px',
    padding: '12px 16px',
    fontSize: '16px',
    color: '#ffffff',
    backgroundColor: 'rgba(15, 23, 42, 0.6)',
    border: '1px solid rgba(59, 130, 246, 0.3)',
    borderRadius: '8px',
    outline: 'none',
    transition: 'all 0.3s ease',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  inputFocus: {
    borderColor: '#3b82f6',
    boxShadow: '0 0 0 3px rgba(59, 130, 246, 0.1)',
    backgroundColor: 'rgba(15, 23, 42, 0.8)',
  } as React.CSSProperties,

  inputHover: {
    borderColor: 'rgba(59, 130, 246, 0.5)',
    backgroundColor: 'rgba(15, 23, 42, 0.7)',
  } as React.CSSProperties,

  checkbox: {
    color: '#ffffff',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  link: {
    color: '#3b82f6',
    textDecoration: 'none',
  } as React.CSSProperties,

  registerButton: {
    width: '100%',
    height: '48px',
    backgroundColor: '#3b82f6',
    border: 'none',
    borderRadius: '8px',
    color: '#ffffff',
    fontSize: '16px',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    marginTop: '16px',
  } as React.CSSProperties,

  registerButtonHover: {
    backgroundColor: '#2563eb',
    transform: 'translateY(-1px)',
    boxShadow: '0 4px 12px rgba(59, 130, 246, 0.3)',
  } as React.CSSProperties,

  divider: {
    textAlign: 'center' as const,
    margin: '24px 0',
    color: 'rgba(255, 255, 255, 0.5)',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  wechatButton: {
    width: '100%',
    height: '48px',
    backgroundColor: 'rgba(30, 41, 59, 0.8)',
    border: '1px solid rgba(59, 130, 246, 0.3)',
    borderRadius: '8px',
    color: '#ffffff',
    fontSize: '16px',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  loginLink: {
    textAlign: 'center' as const,
    marginTop: '32px',
    color: 'rgba(255, 255, 255, 0.7)',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  loginButton: {
    color: '#3b82f6',
    textDecoration: 'none',
    marginLeft: '8px',
    cursor: 'pointer',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,
};

export const Register: React.FC<RegisterProps> = ({ onRegisterSuccess, onSwitchToLogin }) => {
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);
  const [inputStates, setInputStates] = useState({
    username: { focused: false, hovered: false },
    email: { focused: false, hovered: false },
    password: { focused: false, hovered: false },
    confirmPassword: { focused: false, hovered: false },
  });
  const [buttonHovered, setButtonHovered] = useState(false);

  const onFinish = async (values: RegisterForm) => {
    setLoading(true);
    try {
      const result = await authService.register({
        username: values.username,
        email: values.email,
        password: values.password
      });

      if (result.success) {
        message.success('注册成功！');
        onRegisterSuccess?.();
        window.location.reload();
      } else {
        message.error(result.message || '注册失败，请重试');
      }
    } catch (error: any) {
      message.error(error.message || '注册失败，请重试');
    } finally {
      setLoading(false);
    }
  };

  const getInputStyle = (field: keyof typeof inputStates) => {
    const state = inputStates[field];
    return {
      ...styles.input,
      ...(state.focused ? styles.inputFocus : {}),
      ...(state.hovered && !state.focused ? styles.inputHover : {}),
    };
  };

  const handleInputFocus = (field: keyof typeof inputStates) => {
    setInputStates(prev => ({
      ...prev,
      [field]: { ...prev[field], focused: true }
    }));
  };

  const handleInputBlur = (field: keyof typeof inputStates) => {
    setInputStates(prev => ({
      ...prev,
      [field]: { ...prev[field], focused: false }
    }));
  };

  const handleInputMouseEnter = (field: keyof typeof inputStates) => {
    setInputStates(prev => ({
      ...prev,
      [field]: { ...prev[field], hovered: true }
    }));
  };

  const handleInputMouseLeave = (field: keyof typeof inputStates) => {
    setInputStates(prev => ({
      ...prev,
      [field]: { ...prev[field], hovered: false }
    }));
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <div style={styles.title}>创建账户</div>
        <div style={styles.subtitle}>开始您的投资之旅</div>
      </div>

      <Form
        form={form}
        name="register"
        onFinish={onFinish}
        layout="vertical"
        size="large"
      >
        <div style={styles.formGroup}>
          <Form.Item
            name="username"
            rules={[
              { required: true, message: '请输入用户名' },
              { min: 3, message: '用户名至少3个字符' },
              { max: 20, message: '用户名不能超过20个字符' },
              { pattern: /^[a-zA-Z0-9_]+$/, message: '用户名只能包含字母、数字和下划线' }
            ]}
            style={{ marginBottom: 0 }}
          >
            <Input
              prefix={<UserOutlined style={{ color: 'rgba(255, 255, 255, 0.5)' }} />}
              placeholder="用户名"
              style={getInputStyle('username')}
              onFocus={() => handleInputFocus('username')}
              onBlur={() => handleInputBlur('username')}
              onMouseEnter={() => handleInputMouseEnter('username')}
              onMouseLeave={() => handleInputMouseLeave('username')}
            />
          </Form.Item>
        </div>

        <div style={styles.formGroup}>
          <Form.Item
            name="email"
            rules={[
              { required: true, message: '请输入邮箱地址' },
              { type: 'email', message: '请输入有效的邮箱地址' }
            ]}
            style={{ marginBottom: 0 }}
          >
            <Input
              prefix={<MailOutlined style={{ color: 'rgba(255, 255, 255, 0.5)' }} />}
              placeholder="邮箱地址"
              style={getInputStyle('email')}
              onFocus={() => handleInputFocus('email')}
              onBlur={() => handleInputBlur('email')}
              onMouseEnter={() => handleInputMouseEnter('email')}
              onMouseLeave={() => handleInputMouseLeave('email')}
            />
          </Form.Item>
        </div>

        <div style={styles.formGroup}>
          <Form.Item
            name="password"
            rules={[
              { required: true, message: '请输入密码' },
              { min: 6, message: '密码至少6个字符' },
              { pattern: /^(?=.*[a-zA-Z])(?=.*\d)/, message: '密码必须包含字母和数字' }
            ]}
            style={{ marginBottom: 0 }}
          >
            <Input.Password
              prefix={<LockOutlined style={{ color: 'rgba(255, 255, 255, 0.5)' }} />}
              placeholder="密码"
              iconRender={(visible: boolean) => (visible ? <EyeTwoTone /> : <EyeInvisibleOutlined />)}
              style={getInputStyle('password')}
              onFocus={() => handleInputFocus('password')}
              onBlur={() => handleInputBlur('password')}
              onMouseEnter={() => handleInputMouseEnter('password')}
              onMouseLeave={() => handleInputMouseLeave('password')}
            />
          </Form.Item>
        </div>

        <div style={styles.formGroup}>
          <Form.Item
            name="confirmPassword"
            dependencies={['password']}
            rules={[
              { required: true, message: '请确认密码' },
              ({ getFieldValue }: any) => ({
                validator(_: any, value: any) {
                  if (!value || getFieldValue('password') === value) {
                    return Promise.resolve();
                  }
                  return Promise.reject(new Error('两次输入的密码不一致'));
                },
              }),
            ]}
            style={{ marginBottom: 0 }}
          >
            <Input.Password
              prefix={<LockOutlined style={{ color: 'rgba(255, 255, 255, 0.5)' }} />}
              placeholder="确认密码"
              iconRender={(visible: boolean) => (visible ? <EyeTwoTone /> : <EyeInvisibleOutlined />)}
              style={getInputStyle('confirmPassword')}
              onFocus={() => handleInputFocus('confirmPassword')}
              onBlur={() => handleInputBlur('confirmPassword')}
              onMouseEnter={() => handleInputMouseEnter('confirmPassword')}
              onMouseLeave={() => handleInputMouseLeave('confirmPassword')}
            />
          </Form.Item>
        </div>

        <Form.Item
          name="agreement"
          valuePropName="checked"
          rules={[
            { required: true, message: '请同意用户协议和隐私政策' }
          ]}
          style={{ marginBottom: 0 }}
        >
          <Checkbox style={styles.checkbox}>
            我已阅读并同意 <a href="#" style={styles.link}>用户协议</a> 和 <a href="#" style={styles.link}>隐私政策</a>
          </Checkbox>
        </Form.Item>

        <Form.Item style={{ marginBottom: 0 }}>
          <button
            type="submit"
            style={{
              ...styles.registerButton,
              ...(buttonHovered ? styles.registerButtonHover : {}),
            }}
            onMouseEnter={() => setButtonHovered(true)}
            onMouseLeave={() => setButtonHovered(false)}
            disabled={loading}
          >
            {loading ? '注册中...' : '创建账户'}
          </button>
        </Form.Item>
      </Form>

      <div style={styles.divider}>
        <span>或</span>
      </div>

      <button style={styles.wechatButton}>
        <WechatOutlined style={{ marginRight: '8px' }} />
        微信注册
      </button>

      <div style={styles.loginLink}>
        已有账户？
        <span style={styles.loginButton} onClick={onSwitchToLogin}>
          立即登录
        </span>
      </div>
    </div>
  );
};

export default Register;
