import React from 'react';

const WatchList = ({ watchlist, loading, onRemove, onRefresh }) => {
  // 格式化价格变化
  const formatPriceChange = (change, changePercent) => {
    if (change === null || change === undefined) return null;

    const changeStr = change >= 0 ? `+${change.toFixed(2)}` : change.toFixed(2);
    const percentStr = changePercent ? ` (${changePercent >= 0 ? '+' : ''}${changePercent.toFixed(2)}%)` : '';

    return {
      text: `${changeStr}${percentStr}`,
      className: change > 0 ? 'price-up' : change < 0 ? 'price-down' : 'price-neutral'
    };
  };

  // 格式化价格
  const formatPrice = (price) => {
    if (price === null || price === undefined) return '--';
    return price.toFixed(2);
  };

  if (loading) {
    return (
      <div className="card">
        <h2 className="section-title">⭐ 我的关注</h2>
        <div className="loading">
          <p>加载中...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="card">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2 className="section-title" style={{ marginBottom: 0 }}>⭐ 我的关注</h2>
        <button className="btn-small btn" onClick={onRefresh}>
          🔄 刷新
        </button>
      </div>

      {watchlist.length === 0 ? (
        <div className="empty-state">
          <div className="empty-state-icon">📈</div>
          <p>还没有关注的股票</p>
          <p style={{ fontSize: '0.9rem', color: '#999' }}>使用上方搜索功能添加股票到关注列表</p>
        </div>
      ) : (
        <div>
          {watchlist.map((stock) => {
            const priceChange = formatPriceChange(stock.change, stock.change_percent);

            return (
              <div key={stock.code} className="stock-item">
                <div className="stock-info">
                  <div className="stock-code">{stock.code}</div>
                  <div className="stock-name">{stock.name}</div>
                </div>

                <div className="stock-price">
                  <div className="current-price">
                    ¥{formatPrice(stock.current_price)}
                  </div>
                  {priceChange && (
                    <div className={`price-change ${priceChange.className}`}>
                      {priceChange.text}
                    </div>
                  )}
                </div>

                <div className="stock-actions">
                  <button
                    className="btn-small btn-remove"
                    onClick={() => onRemove(stock.code)}
                    title="从关注列表移除"
                  >
                    移除
                  </button>
                </div>
              </div>
            );
          })}

          <div style={{ marginTop: '15px', padding: '10px', background: '#f9f9f9', borderRadius: '4px', fontSize: '0.9rem', color: '#666' }}>
            💡 提示: 股票价格数据每分钟更新一次
          </div>
        </div>
      )}
    </div>
  );
};

export default WatchList;
