import React, { useState, useEffect } from 'react';
import './App.css';
import StockSearch from './components/StockSearch';
import WatchList from './components/WatchList';
import MarketOverview from './components/MarketOverview';
import StatsPanel from './components/StatsPanel';
import { createApiUrl, API_ENDPOINTS } from './config/api';

function App() {
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // 获取关注列表
  const fetchWatchlist = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(createApiUrl(API_ENDPOINTS.WATCHLIST));
      if (!response.ok) {
        throw new Error('获取关注列表失败');
      }

      const data = await response.json();
      setWatchlist(data.data || []);
    } catch (err) {
      console.error('获取关注列表错误:', err);
      setError(err.message);
      setWatchlist([]);
    } finally {
      setLoading(false);
    }
  };

  // 添加到关注列表
  const handleAddToWatchlist = async (stockCode) => {
    try {
      const response = await fetch(createApiUrl(API_ENDPOINTS.WATCHLIST), {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ stock_code: stockCode }),
      });

      if (!response.ok) {
        throw new Error('添加失败');
      }

      // 重新获取关注列表
      await fetchWatchlist();
      return true;
    } catch (err) {
      console.error('添加到关注列表错误:', err);
      alert('添加失败: ' + err.message);
      return false;
    }
  };

  // 从关注列表移除
  const handleRemoveFromWatchlist = async (stockCode) => {
    try {
      const response = await fetch(createApiUrl(`${API_ENDPOINTS.WATCHLIST}/${stockCode}`), {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('移除失败');
      }

      // 重新获取关注列表
      await fetchWatchlist();
    } catch (err) {
      console.error('从关注列表移除错误:', err);
      alert('移除失败: ' + err.message);
    }
  };

  // 初始化加载
  useEffect(() => {
    fetchWatchlist();
  }, []);

  // 自动刷新关注列表
  useEffect(() => {
    const interval = setInterval(fetchWatchlist, 60000); // 每分钟刷新一次
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="App">
      <header className="header">
        <h1 className="title">🚀 股票洞察 StockInsight</h1>
        <p className="subtitle">实时股票数据监控与分析平台</p>
      </header>

      <main className="main-content">
        <div className="container">
          {/* 第一行：股票搜索 */}
          <div className="row">
            <StockSearch onAddToWatchlist={handleAddToWatchlist} />
          </div>

          {/* 第二行：关注列表和市场概览 */}
          <div className="row">
            <div className="col-md-6">
              <WatchList 
                watchlist={watchlist}
                loading={loading}
                onRemove={handleRemoveFromWatchlist}
                onRefresh={fetchWatchlist}
              />
            </div>
            <div className="col-md-6">
              <MarketOverview />
            </div>
          </div>

          {/* 第三行：系统统计 */}
          <div className="row">
            <StatsPanel />
          </div>
        </div>
      </main>

      <footer className="footer">
        <p>© 2024 StockInsight - 股票数据仅供参考，投资有风险</p>
      </footer>
    </div>
  );
}

export default App;
