/**
 * ÁôªÂΩïÈ°µÈù¢
 * ‰ΩøÁî®ÊúÄÁÆÄÂçïÁöÑÁôªÂΩïÈÄªËæë
 */

import React, { useState } from 'react';

// ÂÆö‰πâÁªÑ‰ª∂propsÁ±ªÂûã
interface LoginPageProps {
  onLoginSuccess: (token: string) => void;
}

const LoginPage: React.FC<LoginPageProps> = ({ onLoginSuccess }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  // Â§ÑÁêÜË°®ÂçïÊèê‰∫§
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    console.log('=== ÂºÄÂßãÁôªÂΩïÊµÅÁ®ã ===');
    console.log('Áî®Êà∑Âêç:', username);
    console.log('ÂØÜÁ†ÅÈïøÂ∫¶:', password.length);

    if (!username.trim()) {
      alert('ËØ∑ËæìÂÖ•Áî®Êà∑Âêç');
      return;
    }

    if (!password.trim()) {
      alert('ËØ∑ËæìÂÖ•ÂØÜÁ†Å');
      return;
    }

    setLoading(true);
    console.log('ËÆæÁΩÆloading‰∏∫true');

    try {
      console.log('ÂèëÈÄÅÁôªÂΩïËØ∑Ê±Ç...');

      // ÂèëÈÄÅÁôªÂΩïËØ∑Ê±ÇÂà∞ÂêéÁ´Ø
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username.trim(),
          password: password.trim()
        })
      });

      console.log('ÁôªÂΩïÂìçÂ∫îÁä∂ÊÄÅ:', response.status);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('ÁôªÂΩïÂìçÂ∫îÊï∞ÊçÆ:', data);

      if (data.success && data.token) {
        console.log('ÁôªÂΩïÊàêÂäüÔºåËé∑Âæótoken:', data.token);
        console.log('tokenÈïøÂ∫¶:', data.token.length);

        // Ë∞ÉÁî®Áà∂ÁªÑ‰ª∂ÁöÑÊàêÂäüÂõûË∞É
        onLoginSuccess(data.token);
        console.log('Â∑≤Ë∞ÉÁî®onLoginSuccessÂõûË∞É');
      } else {
        console.error('ÁôªÂΩïÂ§±Ë¥•:', data.message || 'Êú™Áü•ÈîôËØØ');
        alert(data.message || 'ÁôªÂΩïÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•Áî®Êà∑ÂêçÂíåÂØÜÁ†Å');
        setLoading(false);
      }
    } catch (error: any) {
      console.error('ÁôªÂΩïËØ∑Ê±ÇÂá∫Èîô:', error);
      alert(error.message || 'ÁôªÂΩïÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
      setLoading(false);
    }

    console.log('=== ÁôªÂΩïÊµÅÁ®ãÁªìÊùü ===');
  };

  // Â§ÑÁêÜËÆ∞‰ΩèÊàëÂ§çÈÄâÊ°ÜÁÇπÂáª
  const handleRememberMeClick = () => {
    setRememberMe(!rememberMe);
  };

  // Â§ÑÁêÜÂøòËÆ∞ÂØÜÁ†ÅÁÇπÂáª
  const handleForgotPasswordClick = (e: React.MouseEvent) => {
    e.preventDefault();
    alert('ÂøòËÆ∞ÂØÜÁ†ÅÂäüËÉΩÂºÄÂèë‰∏≠ÔºåËØ∑ËÅîÁ≥ªÁÆ°ÁêÜÂëòÔºÅ');
  };

  // Â§ÑÁêÜÁ§æ‰∫§ÁôªÂΩïÊåâÈíÆÁÇπÂáª
  const handleSocialLoginClick = (e: React.MouseEvent, platform: string) => {
    e.preventDefault();
    alert(`${platform}ÂäüËÉΩÂºÄÂèë‰∏≠ÔºåÊï¨ËØ∑ÊúüÂæÖÔºÅ`);
  };

  // Â§ÑÁêÜÊ≥®ÂÜåÈìæÊé•ÁÇπÂáª
  const handleRegisterClick = (e: React.MouseEvent) => {
    e.preventDefault();
    alert('Ê≥®ÂÜåÂäüËÉΩÂºÄÂèë‰∏≠ÔºåËØ∑ËÅîÁ≥ªÁÆ°ÁêÜÂëòÔºÅ');
  };

  return (
    <>
      <div className="login-header">
        <div className="login-title">Ê¨¢ËøéÂõûÊù•</div>
        <div className="login-subtitle">ÁôªÂΩïÊÇ®ÁöÑË¥¶Êà∑‰ª•ÁªßÁª≠‰ΩøÁî®</div>
      </div>

      <form id="loginForm" onSubmit={handleSubmit}>
        <div className="form-group">
          <label className="form-label">Áî®Êà∑Âêç</label>
          <div className="input-wrapper">
            <input 
              type="text" 
              className="form-input" 
              placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑Âêç" 
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              autoComplete="new-password"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.06)' }}
            />
            <div className="input-icon">üë§</div>
          </div>
        </div>

        <div className="form-group">
          <label className="form-label">ÂØÜÁ†Å</label>
          <div className="input-wrapper">
            <input 
              type={showPassword ? "text" : "password"} 
              className="form-input" 
              placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              autoComplete="new-password"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.06)' }}
            />
            <div 
              className="input-icon password-toggle" 
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? 'üëÅÔ∏è' : 'üîí'}
            </div>
          </div>
        </div>

        <div className="form-options">
          <label className="remember-me" onClick={handleRememberMeClick}>
            <div className={`custom-checkbox ${rememberMe ? 'checked' : ''}`}></div>
            ËÆ∞‰ΩèÊàë
          </label>
          <button type="button" className="forgot-password" onClick={handleForgotPasswordClick}>ÂøòËÆ∞ÂØÜÁ†ÅÔºü</button>
        </div>

        <button type="submit" className="login-button" disabled={loading}>
          {loading ? (
            <>
              <div className="loading"></div>
              <span>ÁôªÂΩï‰∏≠...</span>
            </>
          ) : (
            <span className="button-text">ÁôªÂΩï</span>
          )}
        </button>
      </form>

      <div className="social-login">
        <div className="divider">
          <span>Êàñ‰ΩøÁî®‰ª•‰∏ãÊñπÂºèÁôªÂΩï</span>
        </div>
        <div className="social-buttons">
          <button 
            type="button" 
            className="social-button"
            onClick={(e) => handleSocialLoginClick(e, 'ÂæÆ‰ø°ÁôªÂΩï')}
          >
            <span>üí¨</span>
            ÂæÆ‰ø°ÁôªÂΩï
          </button>
          <button 
            type="button" 
            className="social-button"
            onClick={(e) => handleSocialLoginClick(e, 'ÊîØ‰ªòÂÆùÁôªÂΩï')}
          >
            <span>üì±</span>
            ÊîØ‰ªòÂÆùÁôªÂΩï
          </button>
        </div>
      </div>

      <div className="register-link">
        ËøòÊ≤°ÊúâË¥¶Âè∑Ôºü<button type="button" className="register-button" onClick={handleRegisterClick}>Á´ãÂç≥Ê≥®ÂÜå</button>
      </div>
    </>
  );
};

export default LoginPage;
