/**
 * Ê≥®ÂÜåÁªÑ‰ª∂
 * ‰ΩøÁî®ÂéüÁîüHTMLÂÖÉÁ¥†ÂíåÂÆåÂÖ®ÂÜÖËÅîÊ†∑ÂºèÔºåÁ°Æ‰øùÊ∑±Ëâ≤ÈÄèÊòéËÉåÊôØÊïàÊûú
 */

import React, { useState } from 'react';
import { message } from 'antd';
import { UserOutlined, MailOutlined, LockOutlined, WechatOutlined } from '@ant-design/icons';
import { useAuth } from '../../contexts/AuthContext';

interface RegisterFormData {
  username: string;
  email: string;
  password: string;
  confirmPassword: string;
  agreement?: boolean;
}

interface RegisterProps {
  onSwitchToLogin: () => void;
  onRegisterSuccess?: () => void;
}

// ÂÆåÂÖ®ÂÜÖËÅîÊ†∑ÂºèÂÆö‰πâ
const styles = {
  container: {
    width: '100%',
    maxWidth: '400px',
    margin: '0 auto',
    padding: '0 20px',
  } as React.CSSProperties,

  header: {
    textAlign: 'center' as const,
    marginBottom: '32px',
  } as React.CSSProperties,

  title: {
    fontSize: '28px',
    fontWeight: 'bold',
    color: '#ffffff',
    marginBottom: '8px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  subtitle: {
    fontSize: '16px',
    color: 'rgba(255, 255, 255, 0.7)',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  form: {
    width: '100%',
  } as React.CSSProperties,

  formGroup: {
    marginBottom: '24px',
  } as React.CSSProperties,

  label: {
    display: 'block',
    fontSize: '14px',
    color: '#ffffff',
    marginBottom: '8px',
    fontWeight: '500',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  inputWrapper: {
    position: 'relative' as const,
    width: '100%',
  } as React.CSSProperties,

  inputIcon: {
    position: 'absolute' as const,
    left: '14px',
    top: '50%',
    transform: 'translateY(-50%)',
    color: 'rgba(59, 130, 246, 0.7)',
    fontSize: '18px',
    zIndex: 2,
    pointerEvents: 'none' as const,
  } as React.CSSProperties,

  input: {
    width: '100%',
    height: '48px',
    padding: '12px 16px 12px 50px',
    fontSize: '16px',
    color: '#ffffff',
    backgroundColor: 'rgba(15, 23, 42, 0.95)',
    border: '1px solid rgba(59, 130, 246, 0.4)',
    borderRadius: '8px',
    outline: 'none',
    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    boxSizing: 'border-box' as const,
    WebkitAppearance: 'none',
    appearance: 'none',
    WebkitTextFillColor: '#ffffff',
    WebkitBoxShadow: '0 0 0 1000px rgba(15, 23, 42, 0.95) inset',
    caretColor: '#3b82f6',
  } as React.CSSProperties,

  inputFocus: {
    borderColor: '#3b82f6',
    boxShadow: '0 0 0 3px rgba(59, 130, 246, 0.25), 0 0 20px rgba(59, 130, 246, 0.15)',
    backgroundColor: 'rgba(15, 23, 42, 0.98)',
    WebkitTextFillColor: '#ffffff',
    WebkitBoxShadow: '0 0 0 1000px rgba(15, 23, 42, 0.98) inset',
    transform: 'translateY(-1px)',
  } as React.CSSProperties,

  inputHover: {
    borderColor: 'rgba(59, 130, 246, 0.6)',
    backgroundColor: 'rgba(15, 23, 42, 0.97)',
    WebkitTextFillColor: '#ffffff',
    WebkitBoxShadow: '0 0 0 1000px rgba(15, 23, 42, 0.97) inset',
    boxShadow: '0 0 10px rgba(59, 130, 246, 0.1)',
  } as React.CSSProperties,

  passwordWrapper: {
    position: 'relative' as const,
    width: '100%',
  } as React.CSSProperties,

  passwordToggle: {
    position: 'absolute' as const,
    right: '12px',
    top: '50%',
    transform: 'translateY(-50%)',
    background: 'none',
    border: 'none',
    color: 'rgba(59, 130, 246, 0.7)',
    cursor: 'pointer',
    fontSize: '16px',
    zIndex: 3,
    padding: '4px',
    borderRadius: '4px',
    transition: 'all 0.2s ease',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '32px',
    height: '32px',
  } as React.CSSProperties,

  agreementWrapper: {
    display: 'flex',
    alignItems: 'flex-start',
    gap: '8px',
    marginBottom: '24px',
  } as React.CSSProperties,

  checkbox: {
    width: '16px',
    height: '16px',
    accentColor: '#3b82f6',
    marginTop: '2px',
  } as React.CSSProperties,

  agreementText: {
    color: 'rgba(255, 255, 255, 0.7)',
    fontSize: '14px',
    lineHeight: '1.5',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  agreementLink: {
    color: '#3b82f6',
    textDecoration: 'none',
    cursor: 'pointer',
  } as React.CSSProperties,

  registerButton: {
    width: '100%',
    height: '48px',
    backgroundColor: '#3b82f6',
    border: 'none',
    borderRadius: '8px',
    color: '#ffffff',
    fontSize: '16px',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    marginBottom: '32px',
  } as React.CSSProperties,

  registerButtonHover: {
    backgroundColor: '#2563eb',
    transform: 'translateY(-1px)',
    boxShadow: '0 4px 12px rgba(59, 130, 246, 0.3)',
  } as React.CSSProperties,

  registerButtonDisabled: {
    backgroundColor: 'rgba(59, 130, 246, 0.5)',
    cursor: 'not-allowed',
    transform: 'none',
    boxShadow: 'none',
  } as React.CSSProperties,

  socialLogin: {
    textAlign: 'center' as const,
  } as React.CSSProperties,

  divider: {
    position: 'relative' as const,
    marginBottom: '24px',
    textAlign: 'center' as const,
    color: 'rgba(255, 255, 255, 0.5)',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  wechatButton: {
    width: '100%',
    height: '48px',
    backgroundColor: 'rgba(30, 41, 59, 0.8)',
    border: '1px solid rgba(59, 130, 246, 0.3)',
    borderRadius: '8px',
    color: '#ffffff',
    fontSize: '16px',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '8px',
    marginBottom: '32px',
  } as React.CSSProperties,

  loginLink: {
    textAlign: 'center' as const,
    color: 'rgba(255, 255, 255, 0.7)',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  loginButton: {
    color: '#3b82f6',
    textDecoration: 'none',
    marginLeft: '8px',
    cursor: 'pointer',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    background: 'none',
    border: 'none',
    padding: '0',
    fontSize: '14px',
  } as React.CSSProperties,

  errorMessage: {
    color: '#ef4444',
    fontSize: '12px',
    marginTop: '4px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,
};

export const Register: React.FC<RegisterProps> = ({ onSwitchToLogin, onRegisterSuccess }) => {
  const [formData, setFormData] = useState<RegisterFormData>({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    agreement: false,
  });
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [errors, setErrors] = useState<Partial<RegisterFormData>>({});
  const [inputStates, setInputStates] = useState({
    username: { focused: false, hovered: false },
    email: { focused: false, hovered: false },
    password: { focused: false, hovered: false },
    confirmPassword: { focused: false, hovered: false },
  });
  const [buttonHovered, setButtonHovered] = useState(false);
  const { register } = useAuth();

  const validateForm = (): boolean => {
    const newErrors: Partial<RegisterFormData> = {};

    if (!formData.username.trim()) {
      newErrors.username = 'ËØ∑ËæìÂÖ•Áî®Êà∑Âêç';
    } else if (formData.username.length < 3) {
      newErrors.username = 'Áî®Êà∑ÂêçËá≥Â∞ëÈúÄË¶Å3‰∏™Â≠óÁ¨¶';
    } else if (!/^[a-zA-Z0-9_]+$/.test(formData.username)) {
      newErrors.username = 'Áî®Êà∑ÂêçÂè™ËÉΩÂåÖÂê´Â≠óÊØç„ÄÅÊï∞Â≠óÂíå‰∏ãÂàíÁ∫ø';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'ËØ∑ËæìÂÖ•ÈÇÆÁÆ±Âú∞ÂùÄ';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'ËØ∑ËæìÂÖ•ÊúâÊïàÁöÑÈÇÆÁÆ±Âú∞ÂùÄ';
    }

    if (!formData.password) {
      newErrors.password = 'ËØ∑ËæìÂÖ•ÂØÜÁ†Å';
    } else if (formData.password.length < 6) {
      newErrors.password = 'ÂØÜÁ†ÅËá≥Â∞ëÈúÄË¶Å6‰∏™Â≠óÁ¨¶';
    } else if (!/(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/.test(formData.password)) {
      newErrors.password = 'ÂØÜÁ†ÅÂøÖÈ°ªÂåÖÂê´Â§ßÂ∞èÂÜôÂ≠óÊØçÂíåÊï∞Â≠ó';
    }

    if (!formData.confirmPassword) {
      newErrors.confirmPassword = 'ËØ∑Á°ÆËÆ§ÂØÜÁ†Å';
    } else if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = '‰∏§Ê¨°ËæìÂÖ•ÁöÑÂØÜÁ†Å‰∏ç‰∏ÄËá¥';
    }

    if (!formData.agreement) {
      message.error('ËØ∑ÂêåÊÑèÁî®Êà∑ÂçèËÆÆÂíåÈöêÁßÅÊîøÁ≠ñ');
      return false;
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setLoading(true);

    try {
      const success = await register(formData.username, formData.email, formData.password);
      if (success) {
        onRegisterSuccess?.();
      }
    } catch (error) {
      message.error('Ê≥®ÂÜåÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (field: keyof RegisterFormData, value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: undefined }));
    }
  };

  const getInputStyle = (field: 'username' | 'email' | 'password' | 'confirmPassword') => {
    const state = inputStates[field];
    const baseStyle = {
      ...styles.input,
      ...(state.focused ? styles.inputFocus : {}),
      ...(state.hovered && !state.focused ? styles.inputHover : {}),
      ...(errors[field] ? { borderColor: '#ef4444' } : {}),
    };

    // ‰∏∫ÂØÜÁ†ÅËæìÂÖ•Ê°ÜÊ∑ªÂä†Âè≥‰æß paddingÔºå‰∏∫ÂàáÊç¢ÊåâÈíÆÁïôÂá∫Á©∫Èó¥
    if (field === 'password' || field === 'confirmPassword') {
      baseStyle.paddingRight = '50px';
    }

    return baseStyle;
  };

  const handleInputFocus = (field: 'username' | 'email' | 'password' | 'confirmPassword') => {
    setInputStates(prev => ({
      ...prev,
      [field]: { ...prev[field], focused: true }
    }));
  };

  const handleInputBlur = (field: 'username' | 'email' | 'password' | 'confirmPassword') => {
    setInputStates(prev => ({
      ...prev,
      [field]: { ...prev[field], focused: false }
    }));
  };

  const handleInputMouseEnter = (field: 'username' | 'email' | 'password' | 'confirmPassword') => {
    setInputStates(prev => ({
      ...prev,
      [field]: { ...prev[field], hovered: true }
    }));
  };

  const handleInputMouseLeave = (field: 'username' | 'email' | 'password' | 'confirmPassword') => {
    setInputStates(prev => ({
      ...prev,
      [field]: { ...prev[field], hovered: false }
    }));
  };

  const handleWechatRegister = () => {
    message.info('ÂæÆ‰ø°Ê≥®ÂÜåÂäüËÉΩÂºÄÂèë‰∏≠ÔºåÊï¨ËØ∑ÊúüÂæÖÔºÅ');
  };

  const handleUserAgreement = () => {
    message.info('Áî®Êà∑ÂçèËÆÆÈ°µÈù¢ÂºÄÂèë‰∏≠ÔºåÊï¨ËØ∑ÊúüÂæÖÔºÅ');
  };

  const handlePrivacyPolicy = () => {
    message.info('ÈöêÁßÅÊîøÁ≠ñÈ°µÈù¢ÂºÄÂèë‰∏≠ÔºåÊï¨ËØ∑ÊúüÂæÖÔºÅ');
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <div style={styles.title}>ÂàõÂª∫Ë¥¶Êà∑</div>
        <div style={styles.subtitle}>ÂºÄÂßãÊÇ®ÁöÑÊäïËµÑ‰πãÊóÖ</div>
      </div>

      <form style={styles.form} onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label style={styles.label}>Áî®Êà∑Âêç</label>
          <div style={styles.inputWrapper}>
            <UserOutlined style={styles.inputIcon} />
            <input
              type="text"
              placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑Âêç"
              value={formData.username}
              onChange={(e) => handleInputChange('username', e.target.value)}
              style={getInputStyle('username')}
              onFocus={() => handleInputFocus('username')}
              onBlur={() => handleInputBlur('username')}
              onMouseEnter={() => handleInputMouseEnter('username')}
              onMouseLeave={() => handleInputMouseLeave('username')}
            />
          </div>
          {errors.username && <div style={styles.errorMessage}>{errors.username}</div>}
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>ÈÇÆÁÆ±Âú∞ÂùÄ</label>
          <div style={styles.inputWrapper}>
            <MailOutlined style={styles.inputIcon} />
            <input
              type="email"
              placeholder="ËØ∑ËæìÂÖ•ÈÇÆÁÆ±Âú∞ÂùÄ"
              value={formData.email}
              onChange={(e) => handleInputChange('email', e.target.value)}
              style={getInputStyle('email')}
              onFocus={() => handleInputFocus('email')}
              onBlur={() => handleInputBlur('email')}
              onMouseEnter={() => handleInputMouseEnter('email')}
              onMouseLeave={() => handleInputMouseLeave('email')}
            />
          </div>
          {errors.email && <div style={styles.errorMessage}>{errors.email}</div>}
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>ÂØÜÁ†Å</label>
          <div style={styles.passwordWrapper}>
            <LockOutlined style={styles.inputIcon} />
            <input
              type={showPassword ? 'text' : 'password'}
              placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å"
              value={formData.password}
              onChange={(e) => handleInputChange('password', e.target.value)}
              style={getInputStyle('password')}
              onFocus={() => handleInputFocus('password')}
              onBlur={() => handleInputBlur('password')}
              onMouseEnter={() => handleInputMouseEnter('password')}
              onMouseLeave={() => handleInputMouseLeave('password')}
            />
            <button
              type="button"
              style={styles.passwordToggle}
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? 'üôà' : 'üëÅÔ∏è'}
            </button>
          </div>
          {errors.password && <div style={styles.errorMessage}>{errors.password}</div>}
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Á°ÆËÆ§ÂØÜÁ†Å</label>
          <div style={styles.passwordWrapper}>
            <LockOutlined style={styles.inputIcon} />
            <input
              type={showConfirmPassword ? 'text' : 'password'}
              placeholder="ËØ∑ÂÜçÊ¨°ËæìÂÖ•ÂØÜÁ†Å"
              value={formData.confirmPassword}
              onChange={(e) => handleInputChange('confirmPassword', e.target.value)}
              style={getInputStyle('confirmPassword')}
              onFocus={() => handleInputFocus('confirmPassword')}
              onBlur={() => handleInputBlur('confirmPassword')}
              onMouseEnter={() => handleInputMouseEnter('confirmPassword')}
              onMouseLeave={() => handleInputMouseLeave('confirmPassword')}
            />
            <button
              type="button"
              style={styles.passwordToggle}
              onClick={() => setShowConfirmPassword(!showConfirmPassword)}
            >
              {showConfirmPassword ? 'üôà' : 'üëÅÔ∏è'}
            </button>
          </div>
          {errors.confirmPassword && <div style={styles.errorMessage}>{errors.confirmPassword}</div>}
        </div>

        <div style={styles.agreementWrapper}>
          <input
            type="checkbox"
            id="agreement"
            checked={formData.agreement}
            onChange={(e) => handleInputChange('agreement', e.target.checked)}
            style={styles.checkbox}
          />
          <div style={styles.agreementText}>
            ÊàëÂêåÊÑèÂπ∂Êé•Âèó{' '}
            <button
              type="button"
              style={styles.agreementLink}
              onClick={handleUserAgreement}
            >
              Áî®Êà∑ÂçèËÆÆ
            </button>
            {' '}Âíå{' '}
            <button
              type="button"
              style={styles.agreementLink}
              onClick={handlePrivacyPolicy}
            >
              ÈöêÁßÅÊîøÁ≠ñ
            </button>
          </div>
        </div>

        <button
          type="submit"
          style={{
            ...styles.registerButton,
            ...(buttonHovered && !loading ? styles.registerButtonHover : {}),
            ...(loading ? styles.registerButtonDisabled : {}),
          }}
          onMouseEnter={() => setButtonHovered(true)}
          onMouseLeave={() => setButtonHovered(false)}
          disabled={loading}
        >
          {loading ? 'Ê≥®ÂÜå‰∏≠...' : 'ÂàõÂª∫Ë¥¶Êà∑'}
        </button>
      </form>

      <div style={styles.socialLogin}>
        <div style={styles.divider}>
          <span>Êàñ</span>
        </div>
        <button style={styles.wechatButton} onClick={handleWechatRegister}>
          <WechatOutlined />
          ÂæÆ‰ø°Ê≥®ÂÜå
        </button>
      </div>

      <div style={styles.loginLink}>
        Â∑≤ÊúâË¥¶Êà∑Ôºü
        <button style={styles.loginButton} onClick={onSwitchToLogin}>
          Á´ãÂç≥ÁôªÂΩï
        </button>
      </div>
    </div>
  );
};
