import React, { useState } from 'react';

const StockSearch = ({ onAddToWatchlist }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // 搜索股票
  const handleSearch = async () => {
    if (!searchTerm.trim()) {
      setError('请输入股票代码或名称');
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const response = await fetch(`/api/stocks/search?q=${encodeURIComponent(searchTerm)}`);
      if (!response.ok) {
        throw new Error('搜索失败');
      }

      const data = await response.json();
      setSearchResults(data.data || []);
    } catch (err) {
      console.error('搜索错误:', err);
      setError(err.message);
      setSearchResults([]);
    } finally {
      setLoading(false);
    }
  };

  // 处理回车键搜索
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  // 添加到关注列表
  const handleAddToWatchlist = async (stockCode) => {
    const success = await onAddToWatchlist(stockCode);
    if (success) {
      // 从搜索结果中移除已添加的股票
      setSearchResults(prev => prev.filter(stock => stock.code !== stockCode));
    }
  };

  return (
    <div className="card">
      <h2 className="section-title">🔍 股票搜索</h2>

      <div className="search-container">
        <input
          type="text"
          className="search-input"
          placeholder="输入股票代码或名称 (如: 000001, 平安银行)"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <button 
          className="btn" 
          onClick={handleSearch}
          disabled={loading}
        >
          {loading ? '搜索中...' : '搜索'}
        </button>
      </div>

      {error && (
        <div className="error">
          {error}
        </div>
      )}

      {searchResults.length > 0 && (
        <div className="search-results">
          {searchResults.map((stock) => (
            <div key={stock.code} className="search-result-item">
              <div className="stock-info">
                <div className="stock-code">{stock.code}</div>
                <div className="stock-name">{stock.name}</div>
              </div>
              <button
                className="btn-small btn-add"
                onClick={() => handleAddToWatchlist(stock.code)}
              >
                添加关注
              </button>
            </div>
          ))}
        </div>
      )}

      {searchResults.length === 0 && searchTerm && !loading && !error && (
        <div className="empty-state">
          <div className="empty-state-icon">🔍</div>
          <p>未找到相关股票，请尝试其他关键词</p>
        </div>
      )}
    </div>
  );
};

export default StockSearch;
