import React from 'react';
import { Row, Col, Tooltip } from 'antd';
import { StockInfo } from '../services/api';
import { calculateStats, formatPercent, getPriceColor } from '../utils/helpers';

interface StatsCardsProps {
  stocks: StockInfo[];
  loading?: boolean;
}

const StatsCards: React.FC<StatsCardsProps> = ({ stocks, loading = false }) => {
  const stats = calculateStats(stocks);

  const getTooltipContent = (type: string) => {
    switch (type) {
      case 'total':
        return (
          <div>
            <div>Ê∂®Ë∑åÂàÜÂ∏ÉÊòéÁªÜÔºö</div>
            <div style={{ color: '#22c55e' }}>üìà ‰∏äÊ∂®Ôºö{stats.risingCount} Âè™</div>
            <div style={{ color: '#ef4444' }}>üìâ ‰∏ãË∑åÔºö{stats.fallingCount} Âè™</div>
            <div style={{ color: '#ffffff' }}>‚û°Ô∏è Âπ≥ÁõòÔºö{stats.flatCount} Âè™</div>
          </div>
        );
      case 'average':
        return (
          <div>
            <div>Ê∂®Ë∑åÂπÖËØ¶ÊÉÖÔºö</div>
            <div style={{ color: '#22c55e' }}>üìà ÊúÄÈ´òÊ∂®ÂπÖÔºö{formatPercent(stats.maxChangePercent)}</div>
            <div style={{ color: '#ef4444' }}>üìâ ÊúÄ‰ΩéË∑åÂπÖÔºö{formatPercent(stats.minChangePercent)}</div>
            <div>üìä Âπ≥ÂùáÊ∂®Ë∑åÂπÖÔºö{formatPercent(stats.avgChangePercent)}</div>
          </div>
        );
      case 'strong':
        return (
          <div>
            <div>Âº∫ÂäøËÇ°ËØ¶ÊÉÖÔºàÊ∂®ÂπÖ > 5%ÔºâÔºö</div>
            {stats.strongStocksList.length > 0 ? (
              stats.strongStocksList.map((stock, index) => (
                <div key={index} style={{ color: '#22c55e' }}>
                  üöÄ {stock.name}({stock.code})Ôºö{formatPercent(stock.change_percent)}
                </div>
              ))
            ) : (
              <div style={{ color: '#888' }}>ÊöÇÊó†Âº∫ÂäøËÇ°</div>
            )}
          </div>
        );
      default:
        return null;
    }
  };

  const cards = [
    {
      title: 'ÂÖ≥Ê≥®ËÇ°Á•®ÊÄªÊï∞',
      icon: 'üìä',
      iconClass: 'icon-chart',
      value: stats.totalCount,
      subtitle: `‚ÜëÊ∂®${stats.risingCount} ‚ÜìË∑å${stats.fallingCount} ‚ÜíÂπ≥${stats.flatCount}`,
      trend: 'neutral',
      tooltipType: 'total'
    },
    {
      title: 'Âπ≥ÂùáÊ∂®Ë∑åÂπÖ',
      icon: stats.avgChangePercent > 0 ? 'üìà' : stats.avgChangePercent < 0 ? 'üìâ' : '‚û°Ô∏è',
      iconClass: 'icon-trend',
      value: formatPercent(stats.avgChangePercent),
      subtitle: '‰ªäÊó•Êï¥‰ΩìË°®Áé∞',
      trend: stats.avgChangePercent > 0 ? 'up' : stats.avgChangePercent < 0 ? 'down' : 'neutral',
      color: getPriceColor(stats.avgChangePercent),
      tooltipType: 'average'
    },
    {
      title: 'Âº∫ÂäøËÇ°',
      icon: stats.strongStocks > 0 ? 'üöÄ' : 'üìä',
      iconClass: 'icon-star',
      value: stats.strongStocks,
      subtitle: 'Ê∂®ÂπÖ > 5%',
      trend: stats.strongStocks > 0 ? 'up' : 'neutral',
      tooltipType: 'strong'
    },
    {
      title: 'ÊúÄÂêéÊõ¥Êñ∞',
      icon: 'üîî',
      iconClass: 'icon-bell',
      value: stats.lastUpdate,
      subtitle: new Date().toLocaleDateString('zh-CN'),
      trend: 'neutral'
    }
  ];

  return (
    <Row gutter={[20, 20]} style={{ marginBottom: 32 }}>
      {cards.map((card, index) => {
        const CardContent = (
          <div className={`stat-card fade-in`} style={{ animationDelay: `${index * 0.1}s` }}>
            <div className="stat-header">
              <span className="stat-title">{card.title}</span>
              <div className={`stat-icon ${card.iconClass}`}>
                {card.icon}
              </div>
            </div>
            <div 
              className="stat-number"
              style={{ 
                color: card.color || '#ffffff'
              }}
            >
              {loading ? '--' : card.value}
            </div>
            <div className={`stat-subtitle ${
              card.trend === 'up' ? 'positive' : 
              card.trend === 'down' ? 'negative' : 'neutral'
            }`}>
              {loading ? 'Âä†ËΩΩ‰∏≠...' : (
                <>
                  {card.trend === 'up' && '‚Üó '}
                  {card.trend === 'down' && '‚Üò '}
                  {card.trend === 'neutral' && 'üìÖ '}
                  {card.subtitle}
                </>
              )}
            </div>
          </div>
        );

        return (
          <Col xs={12} sm={12} md={6} lg={6} xl={6} key={index}>
            {card.tooltipType ? (
              <Tooltip 
                title={getTooltipContent(card.tooltipType)} 
                placement="top"
                overlayStyle={{ maxWidth: '300px' }}
              >
                {CardContent}
              </Tooltip>
            ) : (
              CardContent
            )}
          </Col>
        );
      })}
    </Row>
  );
};

export default StatsCards;
