/**
 * 登录组件
 * 使用原生HTML input元素和完全内联样式
 */

import React, { useState } from 'react';
import { message } from 'antd';
import { UserOutlined, LockOutlined, EyeOutlined, EyeInvisibleOutlined } from '@ant-design/icons';
import { useAuth } from '../../contexts/AuthContext';

interface LoginFormData {
  username: string;
  password: string;
  remember?: boolean;
}

interface LoginProps {
  onSwitchToRegister: () => void;
  onLoginSuccess?: () => void;
}

// 输入框组件
const CustomInput: React.FC<{
  type: string;
  placeholder: string;
  value: string;
  onChange: (value: string) => void;
  icon: React.ReactNode;
  showPasswordToggle?: boolean;
  onTogglePassword?: () => void;
  showPassword?: boolean;
}> = ({ type, placeholder, value, onChange, icon, showPasswordToggle, onTogglePassword, showPassword }) => {
  const [isFocused, setIsFocused] = useState(false);

  // 添加调试日志
  React.useEffect(() => {
    console.log('CustomInput mounted with props:', { type, placeholder });
  }, []);

  const handleFocus = () => {
    setIsFocused(true);
    console.log('Input focused, style:', inputStyle);
  };

  const handleBlur = () => {
    setIsFocused(false);
    console.log('Input blurred, value:', value);
  };

  const containerStyle: React.CSSProperties = {
    position: 'relative',
    width: '100%',
    marginBottom: '24px',
  };

  const inputStyle: React.CSSProperties = {
    width: '100%',
    height: '56px',
    background: 'rgba(255, 255, 255, 0.05)',
    border: isFocused ? '1px solid #3b82f6' : '1px solid rgba(255, 255, 255, 0.1)',
    color: 'white',
    padding: '16px 20px 16px 50px',
    borderRadius: '12px',
    fontSize: '16px',
    outline: 'none',
    transition: 'border-color 0.3s ease',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    boxSizing: 'border-box' as const,
    // 添加浏览器自动填充兼容
    WebkitTextFillColor: 'white',
    WebkitBoxShadow: '0 0 0px 1000px rgba(255, 255, 255, 0.05) inset',
  };

  const iconStyle: React.CSSProperties = {
    position: 'absolute',
    left: '18px',
    top: '50%',
    transform: 'translateY(-50%)',
    color: 'rgba(255, 255, 255, 0.6)',
    fontSize: '18px',
    zIndex: 2,
    pointerEvents: 'none' as const,
  };

  const toggleStyle: React.CSSProperties = {
    position: 'absolute',
    right: '18px',
    top: '50%',
    transform: 'translateY(-50%)',
    color: 'rgba(255, 255, 255, 0.6)',
    fontSize: '18px',
    cursor: 'pointer',
    zIndex: 2,
    padding: '4px',
    borderRadius: '4px',
    transition: 'color 0.2s ease',
  };

  return (
    <div style={containerStyle}>
      <div style={iconStyle}>
        {icon}
      </div>
      <input
        type={type}
        placeholder={placeholder}
        value={value}
        onChange={(e) => {
          onChange(e.target.value);
          console.log('Input changed, new value:', e.target.value);
        }}
        onFocus={handleFocus}
        onBlur={handleBlur}
        style={inputStyle}
        className="login-input"
      />
      {showPasswordToggle && (
        <div 
          style={toggleStyle}
          onClick={onTogglePassword}
          onMouseEnter={(e) => {
            (e.target as HTMLElement).style.color = 'rgba(255, 255, 255, 0.8)';
          }}
          onMouseLeave={(e) => {
            (e.target as HTMLElement).style.color = 'rgba(255, 255, 255, 0.6)';
          }}
        >
          {showPassword ? <EyeInvisibleOutlined /> : <EyeOutlined />}
        </div>
      )}
    </div>
  );
};

export const Login: React.FC<LoginProps> = ({ onSwitchToRegister, onLoginSuccess }) => {
  const { login } = useAuth();
  const [formData, setFormData] = useState<LoginFormData>({
    username: '',
    password: '',
    remember: false,
  });
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  // 添加全局 placeholder 样式
  React.useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      .login-input::placeholder {
        color: rgba(255, 255, 255, 0.4) !important;
        opacity: 1 !important;
      }
      .login-input::-webkit-input-placeholder {
        color: rgba(255, 255, 255, 0.4) !important;
        opacity: 1 !important;
      }
      .login-input::-moz-placeholder {
        color: rgba(255, 255, 255, 0.4) !important;
        opacity: 1 !important;
      }
      .login-input:-ms-input-placeholder {
        color: rgba(255, 255, 255, 0.4) !important;
        opacity: 1 !important;
      }
    `;
    document.head.appendChild(style);
    return () => {
      document.head.removeChild(style);
    };
  }, []);

  const handleInputChange = (field: keyof LoginFormData, value: string | boolean) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.username.trim()) {
      message.error('请输入用户名或邮箱');
      return;
    }

    if (!formData.password.trim()) {
      message.error('请输入密码');
      return;
    }

    setLoading(true);
     try {
       const success = await login(formData.username, formData.password);
       if (success) {
         message.success('登录成功');
         onLoginSuccess?.();
       }
     } catch (error: any) {
       message.error(error.message || '登录失败，请重试');
     } finally {
       setLoading(false);
     }
  };

  const containerStyle: React.CSSProperties = {
    width: '100%',
    maxWidth: '400px',
    margin: '0 auto',
    padding: '0 20px',
  };

  const headerStyle: React.CSSProperties = {
    textAlign: 'center' as const,
    marginBottom: '32px',
  };

  const titleStyle: React.CSSProperties = {
    fontSize: '28px',
    fontWeight: 'bold',
    color: '#ffffff',
    marginBottom: '8px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  };

  const subtitleStyle: React.CSSProperties = {
    fontSize: '16px',
    color: 'rgba(255, 255, 255, 0.7)',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  };

  const formStyle: React.CSSProperties = {
    width: '100%',
  };

  const optionsStyle: React.CSSProperties = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '24px',
  };

  const checkboxWrapperStyle: React.CSSProperties = {
    display: 'flex',
    alignItems: 'center',
    gap: '8px',
  };

  const checkboxStyle: React.CSSProperties = {
    width: '16px',
    height: '16px',
    accentColor: '#3b82f6',
  };

  const checkboxLabelStyle: React.CSSProperties = {
    color: '#ffffff',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  };

  const forgotPasswordStyle: React.CSSProperties = {
    color: '#3b82f6',
    textDecoration: 'none',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    cursor: 'pointer',
    background: 'none',
    border: 'none',
    padding: '0',
  };

  const buttonStyle: React.CSSProperties = {
    width: '100%',
    height: '48px',
    backgroundColor: loading ? 'rgba(59, 130, 246, 0.5)' : '#3b82f6',
    border: 'none',
    borderRadius: '8px',
    color: '#ffffff',
    fontSize: '16px',
    fontWeight: '600',
    cursor: loading ? 'not-allowed' : 'pointer',
    transition: 'all 0.3s ease',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    marginBottom: '32px',
  };

  const dividerStyle: React.CSSProperties = {
    textAlign: 'center' as const,
    margin: '24px 0',
    color: 'rgba(255, 255, 255, 0.5)',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  };

  const wechatButtonStyle: React.CSSProperties = {
    width: '100%',
    height: '48px',
    backgroundColor: 'transparent',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    borderRadius: '8px',
    color: '#ffffff',
    fontSize: '16px',
    fontWeight: '500',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    marginBottom: '32px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '8px',
  };

  const footerStyle: React.CSSProperties = {
    textAlign: 'center' as const,
    color: 'rgba(255, 255, 255, 0.7)',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  };

  const linkStyle: React.CSSProperties = {
    color: '#3b82f6',
    textDecoration: 'none',
    cursor: 'pointer',
  };

  return (
    <div style={containerStyle}>
      <div style={headerStyle}>
        <h1 style={titleStyle}>欢迎回来</h1>
        <p style={subtitleStyle}>登录您的账户以继续使用</p>
      </div>

      <form style={formStyle} onSubmit={handleSubmit}>
        <CustomInput
          type="text"
          placeholder="请输入用户名或邮箱"
          value={formData.username}
          onChange={(value) => handleInputChange('username', value)}
          icon={<UserOutlined />}
        />

        <CustomInput
          type={showPassword ? 'text' : 'password'}
          placeholder="请输入密码"
          value={formData.password}
          onChange={(value) => handleInputChange('password', value)}
          icon={<LockOutlined />}
          showPasswordToggle={true}
          onTogglePassword={() => setShowPassword(!showPassword)}
          showPassword={showPassword}
        />

        <div style={optionsStyle}>
          <div style={checkboxWrapperStyle}>
            <input
              type="checkbox"
              id="remember"
              checked={formData.remember}
              onChange={(e) => handleInputChange('remember', e.target.checked)}
              style={checkboxStyle}
            />
            <label htmlFor="remember" style={checkboxLabelStyle}>记住我</label>
          </div>
          <button type="button" style={forgotPasswordStyle}>
            忘记密码？
          </button>
        </div>

        <button
          type="submit"
          disabled={loading}
          style={buttonStyle}
          onMouseEnter={(e) => {
            if (!loading) {
              (e.target as HTMLElement).style.backgroundColor = '#2563eb';
              (e.target as HTMLElement).style.transform = 'translateY(-1px)';
              (e.target as HTMLElement).style.boxShadow = '0 4px 12px rgba(59, 130, 246, 0.3)';
            }
          }}
          onMouseLeave={(e) => {
            if (!loading) {
              (e.target as HTMLElement).style.backgroundColor = '#3b82f6';
              (e.target as HTMLElement).style.transform = 'translateY(0)';
              (e.target as HTMLElement).style.boxShadow = 'none';
            }
          }}
        >
          {loading ? '登录中...' : '登录'}
        </button>

        <div style={dividerStyle}>
          或使用以下方式登录
        </div>

        <button
          type="button"
          style={wechatButtonStyle}
          onMouseEnter={(e) => {
            (e.target as HTMLElement).style.backgroundColor = 'rgba(255, 255, 255, 0.05)';
            (e.target as HTMLElement).style.borderColor = 'rgba(255, 255, 255, 0.3)';
          }}
          onMouseLeave={(e) => {
            (e.target as HTMLElement).style.backgroundColor = 'transparent';
            (e.target as HTMLElement).style.borderColor = 'rgba(255, 255, 255, 0.2)';
          }}
        >
          <span style={{ color: '#07c160', fontSize: '18px' }}>📱</span>
          微信登录
        </button>

        <div style={footerStyle}>
          还没有账号？{' '}
          <span style={linkStyle} onClick={onSwitchToRegister}>
            立即注册
          </span>
        </div>
      </form>
    </div>
  );
};
