/**
 * Ê≥®ÂÜåÁªÑ‰ª∂
 * ‰ΩøÁî®‰∏éÁôªÂΩïÈ°µÈù¢‰∏ÄËá¥ÁöÑÊ†∑ÂºèÔºåÁ°Æ‰øùËâØÂ•ΩÁöÑÁî®Êà∑‰ΩìÈ™å
 */

import React, { useState } from 'react';
import { message } from 'antd';
import { UserOutlined, MailOutlined, LockOutlined, WechatOutlined } from '@ant-design/icons';
import { useAuth } from '../../contexts/AuthContext';
import './Login.css';

interface RegisterFormData {
  username: string;
  email: string;
  password: string;
  confirmPassword: string;
  agreement?: boolean;
}

interface RegisterProps {
  onSwitchToLogin: () => void;
  onRegisterSuccess?: () => void;
}

export const Register: React.FC<RegisterProps> = ({ onSwitchToLogin, onRegisterSuccess }) => {
  const [formData, setFormData] = useState<RegisterFormData>({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    agreement: false,
  });
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [errors, setErrors] = useState<Partial<RegisterFormData>>({});
  const { register } = useAuth();

  const validateForm = (): boolean => {
    const newErrors: Partial<RegisterFormData> = {};

    if (!formData.username.trim()) {
      newErrors.username = 'ËØ∑ËæìÂÖ•Áî®Êà∑Âêç';
    } else if (formData.username.length < 3) {
      newErrors.username = 'Áî®Êà∑ÂêçËá≥Â∞ëÈúÄË¶Å3‰∏™Â≠óÁ¨¶';
    } else if (!/^[a-zA-Z0-9_]+$/.test(formData.username)) {
      newErrors.username = 'Áî®Êà∑ÂêçÂè™ËÉΩÂåÖÂê´Â≠óÊØç„ÄÅÊï∞Â≠óÂíå‰∏ãÂàíÁ∫ø';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'ËØ∑ËæìÂÖ•ÈÇÆÁÆ±Âú∞ÂùÄ';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'ËØ∑ËæìÂÖ•ÊúâÊïàÁöÑÈÇÆÁÆ±Âú∞ÂùÄ';
    }

    if (!formData.password) {
      newErrors.password = 'ËØ∑ËæìÂÖ•ÂØÜÁ†Å';
    } else if (formData.password.length < 6) {
      newErrors.password = 'ÂØÜÁ†ÅËá≥Â∞ëÈúÄË¶Å6‰∏™Â≠óÁ¨¶';
    } else if (!/(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/.test(formData.password)) {
      newErrors.password = 'ÂØÜÁ†ÅÂøÖÈ°ªÂåÖÂê´Â§ßÂ∞èÂÜôÂ≠óÊØçÂíåÊï∞Â≠ó';
    }

    if (!formData.confirmPassword) {
      newErrors.confirmPassword = 'ËØ∑Á°ÆËÆ§ÂØÜÁ†Å';
    } else if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = '‰∏§Ê¨°ËæìÂÖ•ÁöÑÂØÜÁ†Å‰∏ç‰∏ÄËá¥';
    }

    if (!formData.agreement) {
      message.error('ËØ∑ÂêåÊÑèÁî®Êà∑ÂçèËÆÆÂíåÈöêÁßÅÊîøÁ≠ñ');
      return false;
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setLoading(true);

    try {
      const success = await register(formData.username, formData.email, formData.password);
      if (success) {
        onRegisterSuccess?.();
      }
    } catch (error) {
      message.error('Ê≥®ÂÜåÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (field: keyof RegisterFormData, value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: undefined }));
    }
  };

  const handleWechatRegister = () => {
    message.info('ÂæÆ‰ø°Ê≥®ÂÜåÂäüËÉΩÂºÄÂèë‰∏≠ÔºåÊï¨ËØ∑ÊúüÂæÖÔºÅ');
  };

  const handleUserAgreement = () => {
    message.info('Áî®Êà∑ÂçèËÆÆÈ°µÈù¢ÂºÄÂèë‰∏≠ÔºåÊï¨ËØ∑ÊúüÂæÖÔºÅ');
  };

  const handlePrivacyPolicy = () => {
    message.info('ÈöêÁßÅÊîøÁ≠ñÈ°µÈù¢ÂºÄÂèë‰∏≠ÔºåÊï¨ËØ∑ÊúüÂæÖÔºÅ');
  };

  return (
    <>
      <div className="login-header">
        <div className="login-title">ÂàõÂª∫Ë¥¶Êà∑</div>
        <div className="login-subtitle">ÂºÄÂßãÊÇ®ÁöÑÊäïËµÑ‰πãÊóÖ</div>
      </div>

      <form id="registerForm" onSubmit={handleSubmit}>
        <div className="form-group">
          <label className="form-label">Áî®Êà∑Âêç</label>
          <div className="input-wrapper">
            <input 
              type="text" 
              className="form-input" 
              placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑Âêç" 
              value={formData.username}
              onChange={(e) => handleInputChange('username', e.target.value)}
              required
              autoComplete="off"
            />
            <div className="input-icon">üë§</div>
          </div>
          {errors.username && <div className="error-message">{errors.username}</div>}
        </div>

        <div className="form-group">
          <label className="form-label">ÈÇÆÁÆ±Âú∞ÂùÄ</label>
          <div className="input-wrapper">
            <input 
              type="email" 
              className="form-input" 
              placeholder="ËØ∑ËæìÂÖ•ÈÇÆÁÆ±Âú∞ÂùÄ" 
              value={formData.email}
              onChange={(e) => handleInputChange('email', e.target.value)}
              required
              autoComplete="off"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.06)' }}
            />
            <div className="input-icon">üìß</div>
          </div>
          {errors.email && <div className="error-message">{errors.email}</div>}
        </div>

        <div className="form-group">
          <label className="form-label">ÂØÜÁ†Å</label>
          <div className="input-wrapper">
            <input 
              type={showPassword ? "text" : "password"} 
              className="form-input" 
              placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å" 
              value={formData.password}
              onChange={(e) => handleInputChange('password', e.target.value)}
              required
              autoComplete="new-password"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.06)' }}
            />
            <div 
              className="input-icon password-toggle" 
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? 'üëÅÔ∏è' : 'üîí'}
            </div>
          </div>
          {errors.password && <div className="error-message">{errors.password}</div>}
        </div>

        <div className="form-group">
          <label className="form-label">Á°ÆËÆ§ÂØÜÁ†Å</label>
          <div className="input-wrapper">
            <input 
              type={showConfirmPassword ? "text" : "password"} 
              className="form-input" 
              placeholder="ËØ∑ÂÜçÊ¨°ËæìÂÖ•ÂØÜÁ†Å" 
              value={formData.confirmPassword}
              onChange={(e) => handleInputChange('confirmPassword', e.target.value)}
              required
              autoComplete="new-password"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.06)' }}
            />
            <div 
              className="input-icon password-toggle" 
              onClick={() => setShowConfirmPassword(!showConfirmPassword)}
            >
              {showConfirmPassword ? 'üëÅÔ∏è' : 'üîí'}
            </div>
          </div>
          {errors.confirmPassword && <div className="error-message">{errors.confirmPassword}</div>}
        </div>

        <div className="form-options" style={{ justifyContent: 'flex-start', marginBottom: '30px' }}>
          <div className="remember-me">
            <div 
              className={`custom-checkbox ${formData.agreement ? 'checked' : ''}`}
              onClick={() => handleInputChange('agreement', !formData.agreement)}
              style={{ cursor: 'pointer' }}
            ></div>
            <span 
              style={{ color: 'rgba(255, 255, 255, 0.7)' }}
              onClick={() => handleInputChange('agreement', !formData.agreement)}
            >
              ÊàëÂêåÊÑèÂπ∂Êé•Âèó
              <button type="button" className="forgot-password" style={{ marginLeft: '5px' }} onClick={handleUserAgreement}>Áî®Êà∑ÂçèËÆÆ</button>
              Âíå
              <button type="button" className="forgot-password" style={{ marginLeft: '5px' }} onClick={handlePrivacyPolicy}>ÈöêÁßÅÊîøÁ≠ñ</button>
            </span>
          </div>
        </div>

        <button type="submit" className="login-button" disabled={loading}>
          {loading ? (
            <>
              <div className="loading"></div>
              <span>Ê≥®ÂÜå‰∏≠...</span>
            </>
          ) : (
            <span className="button-text">ÂàõÂª∫Ë¥¶Êà∑</span>
          )}
        </button>
      </form>

      <div className="social-login">
        <div className="divider">
          <span>Êàñ‰ΩøÁî®‰ª•‰∏ãÊñπÂºèÊ≥®ÂÜå</span>
        </div>
        <div className="social-buttons">
          <button 
            type="button" 
            className="social-button"
            onClick={handleWechatRegister}
          >
            <span>üí¨</span>
            ÂæÆ‰ø°Ê≥®ÂÜå
          </button>
        </div>
      </div>

      <div className="register-link">
        Â∑≤ÊúâË¥¶Êà∑Ôºü<button type="button" className="register-button" onClick={onSwitchToLogin}>Á´ãÂç≥ÁôªÂΩï</button>
      </div>
    </>
  );
};
