import React, { useState, useEffect } from 'react';
import { createApiUrl, API_ENDPOINTS } from '../config/api';

const StatsPanel = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [timeRange, setTimeRange] = useState(24); // ÈªòËÆ§24Â∞èÊó∂

  // Ëé∑ÂèñÁªüËÆ°Êï∞ÊçÆ
  const fetchStats = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(createApiUrl(`${API_ENDPOINTS.STATS}?hours=${timeRange}`));
      if (!response.ok) {
        throw new Error('Ëé∑ÂèñÁªüËÆ°Êï∞ÊçÆÂ§±Ë¥•');
      }

      const data = await response.json();
      setStats(data.data);
    } catch (err) {
      console.error('Ëé∑ÂèñÁªüËÆ°Êï∞ÊçÆÈîôËØØ:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStats();
  }, [timeRange]);

  // Ëá™Âä®Âà∑Êñ∞
  useEffect(() => {
    const interval = setInterval(fetchStats, 60000); // ÊØèÂàÜÈíüÂà∑Êñ∞‰∏ÄÊ¨°
    return () => clearInterval(interval);
  }, [timeRange]);

  const formatNumber = (num) => {
    if (num === null || num === undefined) return '--';
    return num.toLocaleString();
  };

  const formatPercentage = (num) => {
    if (num === null || num === undefined) return '--';
    return `${num.toFixed(1)}%`;
  };

  const formatTime = (ms) => {
    if (ms === null || ms === undefined) return '--';
    return `${ms.toFixed(0)}ms`;
  };

  if (loading && !stats) {
    return (
      <div className="card">
        <h2 className="section-title">üìä Á≥ªÁªüÁªüËÆ°</h2>
        <div className="loading">
          <p>Âä†ËΩΩÁªüËÆ°Êï∞ÊçÆ‰∏≠...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="card">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2 className="section-title" style={{ marginBottom: 0 }}>üìä Á≥ªÁªüÁªüËÆ°</h2>
        <select 
          value={timeRange} 
          onChange={(e) => setTimeRange(Number(e.target.value))}
          style={{ padding: '5px 10px', borderRadius: '4px', border: '1px solid #ddd' }}
        >
          <option value={1}>ÊúÄËøë1Â∞èÊó∂</option>
          <option value={6}>ÊúÄËøë6Â∞èÊó∂</option>
          <option value={24}>ÊúÄËøë24Â∞èÊó∂</option>
          <option value={168}>ÊúÄËøë7Â§©</option>
        </select>
      </div>

      {error ? (
        <div className="error">
          {error}
          <button 
            className="btn-small btn" 
            onClick={fetchStats}
            style={{ marginLeft: '10px' }}
          >
            ÈáçËØï
          </button>
        </div>
      ) : (
        <div>
          {/* API ÁªüËÆ° */}
          {stats?.api && (
            <div>
              <h3 style={{ fontSize: '1.1rem', marginBottom: '15px', color: '#333' }}>API Ë∞ÉÁî®ÁªüËÆ°</h3>
              <div className="stats-grid">
                <div className="stat-item">
                  <div className="stat-value">{formatNumber(stats.api.total_requests)}</div>
                  <div className="stat-label">ÊÄªËØ∑Ê±ÇÊï∞</div>
                </div>
                <div className="stat-item">
                  <div className="stat-value">{formatPercentage(stats.api.success_rate)}</div>
                  <div className="stat-label">ÊàêÂäüÁéá</div>
                </div>
                <div className="stat-item">
                  <div className="stat-value">{formatTime(stats.api.avg_response_time)}</div>
                  <div className="stat-label">Âπ≥ÂùáÂìçÂ∫îÊó∂Èó¥</div>
                </div>
              </div>
            </div>
          )}

          {/* ÁºìÂ≠òÁªüËÆ° */}
          {stats?.cache && (
            <div style={{ marginTop: '25px' }}>
              <h3 style={{ fontSize: '1.1rem', marginBottom: '15px', color: '#333' }}>ÁºìÂ≠òÁªüËÆ°</h3>
              <div className="stats-grid">
                <div className="stat-item">
                  <div className="stat-value">{formatNumber(stats.cache.total_entries)}</div>
                  <div className="stat-label">ÊÄªÁºìÂ≠òÊù°ÁõÆ</div>
                </div>
                <div className="stat-item">
                  <div className="stat-value">{formatNumber(stats.cache.valid_entries)}</div>
                  <div className="stat-label">ÊúâÊïàÊù°ÁõÆ</div>
                </div>
                <div className="stat-item">
                  <div className="stat-value">{formatPercentage(stats.cache.hit_rate)}</div>
                  <div className="stat-label">ÂëΩ‰∏≠Áéá</div>
                </div>
              </div>
            </div>
          )}

          <div style={{ marginTop: '15px', padding: '10px', background: '#f9f9f9', borderRadius: '4px', fontSize: '0.9rem', color: '#666' }}>
            üìà Êï∞ÊçÆÊØèÂàÜÈíüËá™Âä®Êõ¥Êñ∞ | ÊúÄÂêéÊõ¥Êñ∞: {new Date().toLocaleTimeString()}
          </div>
        </div>
      )}
    </div>
  );
};

export default StatsPanel;
