/**
 * 认证上下文
 * 提供全局的用户状态管理
 */

import React, { createContext, useContext, useEffect, useState, ReactNode } from 'react';
import { authService, User } from '../services/authService';
import { tokenManager } from '../services/api';

interface AuthContextType {
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  login: (username: string, password: string) => Promise<boolean>;
  register: (username: string, email: string, password: string) => Promise<boolean>;
  logout: () => Promise<void>;
  refreshUser: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  // 使用user状态和token存在性共同判断是否已认证
  // 这样可以确保只有在用户信息和token都存在时才认为用户已登录
  const isAuthenticated = !!user && tokenManager.isAuthenticated();

  // 初始化时验证令牌
  useEffect(() => {
    const initAuth = async () => {
      setIsLoading(true);

      try {
        // 检查localStorage中是否有token
        if (tokenManager.isAuthenticated()) {
          console.log('发现本地存储的token，正在验证...');

          // 尝试从localStorage恢复用户信息
          const storedUserInfo = localStorage.getItem('userInfo');
          if (storedUserInfo) {
            try {
              const parsedUserInfo = JSON.parse(storedUserInfo);
              setUser(parsedUserInfo);
              console.log('从localStorage恢复用户信息:', parsedUserInfo);
            } catch (e) {
              console.error('解析localStorage中的用户信息失败:', e);
            }
          }

          // 验证token有效性
          const result = await authService.verifyToken();
          if (result.success && result.user) {
            console.log('Token验证成功，用户已登录:', result.user);
            setUser(result.user);

            // 更新localStorage中的用户信息
            localStorage.setItem('userInfo', JSON.stringify(result.user));
          } else {
            console.log('Token验证失败，清除无效token');
            // 清除无效的token和用户信息
            tokenManager.removeToken();
            localStorage.removeItem('userInfo');
            setUser(null);
          }
        } else {
          console.log('未找到token，用户未登录');
          localStorage.removeItem('userInfo');
          setUser(null);
        }
      } catch (error) {
        console.error('初始化认证时出错:', error);
        // 出错时清除token和用户信息
        tokenManager.removeToken();
        localStorage.removeItem('userInfo');
        setUser(null);
      } finally {
        setIsLoading(false);
      }
    };

    initAuth();
  }, []);

  const login = async (username: string, password: string): Promise<boolean> => {
    try {
      const result = await authService.login({ username, password });
      if (result.success && result.data) {
        // 登录成功，设置用户信息
        setUser(result.data.user);

        // 保存用户信息到localStorage
        localStorage.setItem('userInfo', JSON.stringify(result.data.user));

        return true;
      }
      return false;
    } catch (error) {
      console.error('登录失败:', error);
      return false;
    }
  };

  const register = async (username: string, email: string, password: string): Promise<boolean> => {
    try {
      const result = await authService.register({ username, email, password });
      if (result.success && result.data) {
        setUser(result.data.user);
        return true;
      }
      return false;
    } catch (error) {
      console.error('注册失败:', error);
      return false;
    }
  };

  const logout = async (): Promise<void> => {
    try {
      await authService.logout();
      // 登出时清除用户信息
      setUser(null);

      // 清除localStorage中的用户信息
      localStorage.removeItem('userInfo');
    } catch (error) {
      console.error('登出失败:', error);
    }
  };

  const refreshUser = async (): Promise<void> => {
    if (tokenManager.isAuthenticated()) {
      const result = await authService.getProfile();
      if (result.success && result.user) {
        setUser(result.user);
      }
    }
  };

  const value: AuthContextType = {
    user,
    isAuthenticated,
    isLoading,
    login,
    register,
    logout,
    refreshUser,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = (): AuthContextType => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
