/**
 * 登录组件
 * 使用原生HTML元素和完全内联样式，确保深色透明背景效果
 */

import React, { useState } from 'react';
import { message } from 'antd';
import { UserOutlined, LockOutlined, WechatOutlined } from '@ant-design/icons';
import { useAuth } from '../../contexts/AuthContext';

interface LoginFormData {
  username: string;
  password: string;
  remember?: boolean;
}

interface LoginProps {
  onSwitchToRegister: () => void;
  onLoginSuccess?: () => void;
}

// 样式定义
const styles = {
  container: {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #0a0b0d 0%, #1a1b23 100%)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '20px',
  } as React.CSSProperties,

  card: {
    background: 'rgba(255, 255, 255, 0.05)',
    backdropFilter: 'blur(10px)',
    border: '1px solid rgba(255, 255, 255, 0.1)',
    borderRadius: '16px',
    padding: '40px',
    width: '100%',
    maxWidth: '400px',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
  } as React.CSSProperties,

  title: {
    fontSize: '28px',
    fontWeight: 'bold',
    color: '#ffffff',
    textAlign: 'center' as const,
    marginBottom: '8px',
  } as React.CSSProperties,

  subtitle: {
    fontSize: '16px',
    color: 'rgba(255, 255, 255, 0.7)',
    textAlign: 'center' as const,
    marginBottom: '32px',
  } as React.CSSProperties,

  form: {
    display: 'flex',
    flexDirection: 'column' as const,
    gap: '20px',
  } as React.CSSProperties,

  inputGroup: {
    position: 'relative' as const,
    display: 'flex',
    flexDirection: 'column' as const,
    gap: '8px',
  } as React.CSSProperties,

  label: {
    fontSize: '14px',
    color: 'rgba(255, 255, 255, 0.8)',
    fontWeight: '500',
  } as React.CSSProperties,

  inputContainer: {
    position: 'relative' as const,
  } as React.CSSProperties,

  inputIcon: {
    position: 'absolute' as const,
    left: '18px',
    top: '50%',
    transform: 'translateY(-50%)',
    fontSize: '18px',
    color: 'rgba(255, 255, 255, 0.5)',
    zIndex: 1,
  } as React.CSSProperties,

  input: {
    width: '100%',
    height: '56px',
    padding: '16px 20px 16px 50px',
    fontSize: '16px',
    color: 'white',
    background: 'rgba(255, 255, 255, 0.05)',
    border: '1px solid rgba(255, 255, 255, 0.1)',
    borderRadius: '12px',
    outline: 'none',
    transition: 'border-color 0.3s ease',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    boxSizing: 'border-box' as const,
  } as React.CSSProperties,

  inputFocus: {
    borderColor: '#4096ff',
  } as React.CSSProperties,

  passwordToggle: {
    position: 'absolute' as const,
    right: '18px',
    top: '50%',
    transform: 'translateY(-50%)',
    background: 'none',
    border: 'none',
    color: 'rgba(255, 255, 255, 0.6)',
    cursor: 'pointer',
    padding: '4px',
    borderRadius: '4px',
    transition: 'color 0.3s ease',
    fontSize: '16px',
    width: '24px',
    height: '24px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 3,
  } as React.CSSProperties,

  formOptions: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '24px',
  } as React.CSSProperties,

  checkboxWrapper: {
    display: 'flex',
    alignItems: 'center',
    gap: '8px',
  } as React.CSSProperties,

  checkbox: {
    width: '16px',
    height: '16px',
    accentColor: '#3b82f6',
  } as React.CSSProperties,

  checkboxLabel: {
    color: '#ffffff',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  forgotPassword: {
    color: '#3b82f6',
    textDecoration: 'none',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    cursor: 'pointer',
    background: 'none',
    border: 'none',
    padding: '0',
  } as React.CSSProperties,

  loginButton: {
    width: '100%',
    height: '48px',
    backgroundColor: '#3b82f6',
    border: 'none',
    borderRadius: '8px',
    color: '#ffffff',
    fontSize: '16px',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    marginBottom: '32px',
  } as React.CSSProperties,

  loginButtonHover: {
    backgroundColor: '#2563eb',
    transform: 'translateY(-1px)',
    boxShadow: '0 4px 12px rgba(59, 130, 246, 0.3)',
  } as React.CSSProperties,

  loginButtonDisabled: {
    backgroundColor: 'rgba(59, 130, 246, 0.5)',
    cursor: 'not-allowed',
    transform: 'none',
    boxShadow: 'none',
  } as React.CSSProperties,

  socialLogin: {
    textAlign: 'center' as const,
  } as React.CSSProperties,

  divider: {
    position: 'relative' as const,
    marginBottom: '24px',
    textAlign: 'center' as const,
