/**
 * 路由配置文件
 * 增强的路由守卫和认证状态处理
 */

import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { tokenManager } from '../services/api';

// 布局组件
import AuthLayout from '../layouts/AuthLayout';
import DashboardLayout from '../layouts/DashboardLayout';

// 页面组件
import HomePage from '../pages/HomePage';
import LoginPage from '../pages/LoginPage';
import RegisterPage from '../pages/RegisterPage';
import DashboardPage from '../pages/DashboardPage';

// 加载组件
const LoadingScreen = () => (
  <div className="loading-container">
    <div className="loading-spinner"></div>
    <p>加载中...</p>
  </div>
);

// 路由守卫组件 - 保护需要登录的路由
const ProtectedRoute: React.FC<{ element: React.ReactElement }> = ({ element }) => {
  const { isAuthenticated, isLoading, verifySession } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [isVerifying, setIsVerifying] = useState(false);

  // 检查登录状态是否有效
  useEffect(() => {
    const checkAuth = async () => {
      // 如果已经在加载中，不重复验证
      if (isLoading || isVerifying) return;

      // 如果未认证，尝试验证会话
      if (!isAuthenticated) {
        console.log('ProtectedRoute: 尝试验证会话...');
        setIsVerifying(true);

        try {
          const verified = await verifySession?.() || false;

          if (!verified) {
            console.log('未检测到有效登录状态，重定向到登录页面');
            // 保存当前路径，以便登录后返回
            sessionStorage.setItem('redirectAfterLogin', location.pathname);
            navigate('/login', { replace: true });
          } else {
            console.log('会话验证成功');
          }
        } catch (error) {
          console.error('会话验证出错', error);
          navigate('/login', { replace: true });
        } finally {
          setIsVerifying(false);
        }
      }
    };

    checkAuth();
  }, [isAuthenticated, isLoading, navigate, location, verifySession]);

  // 如果正在加载认证状态或验证中，显示加载中
  if (isLoading || isVerifying) {
    return <LoadingScreen />;
  }

  // 如果未登录，重定向到登录页面
  return isAuthenticated ? element : <Navigate to="/login" replace />;
};

// 公共路由组件 - 已登录用户会被重定向到仪表盘
const PublicRoute: React.FC<{ element: React.ReactElement }> = ({ element }) => {
  const { isAuthenticated, isLoading, verifySession } = useAuth();
  const navigate = useNavigate();
  const [isVerifying, setIsVerifying] = useState(false);

  // 检查token和用户信息是否存在
  useEffect(() => {
    const checkAuth = async () => {
      // 如果已经在加载中，不重复验证
      if (isLoading || isVerifying) return;

      // 如果本地状态显示已登录，验证会话
      if (isAuthenticated) {
        console.log('PublicRoute: 验证会话...');
        setIsVerifying(true);

        try {
          const verified = await verifySession?.() || true;

          if (verified) {
            console.log('检测到有效登录状态，重定向到仪表盘');
            // 获取登录后的重定向路径
            const redirectPath = sessionStorage.getItem('redirectAfterLogin') || '/dashboard';
            sessionStorage.removeItem('redirectAfterLogin');
            navigate(redirectPath, { replace: true });
          }
        } catch (error) {
          console.error('会话验证出错', error);
        } finally {
          setIsVerifying(false);
        }
      }
    };

    checkAuth();
  }, [isAuthenticated, isLoading, navigate, verifySession]);

  // 如果正在加载认证状态或验证中，显示加载中
  if (isLoading || isVerifying) {
    return <LoadingScreen />;
  }

  // 如果已登录，重定向到仪表盘
  return isAuthenticated ? <Navigate to="/dashboard" replace /> : element;
};

// 路由配置组件
const AppRoutes: React.FC = () => {
  return (
    <BrowserRouter>
      <Routes>
        {/* 主页路由 */}
        <Route path="/" element={<HomePage />} />

        {/* 认证相关路由 - 使用AuthLayout */}
        <Route element={<AuthLayout />}>
          <Route path="/login" element={<PublicRoute element={<LoginPage />} />} />
          <Route path="/register" element={<PublicRoute element={<RegisterPage />} />} />
        </Route>

        {/* 仪表盘相关路由 - 使用DashboardLayout */}
        <Route element={<DashboardLayout />}>
          <Route path="/dashboard" element={<ProtectedRoute element={<DashboardPage />} />} />
          {/* 可以添加更多仪表盘相关的子路由 */}
        </Route>

        {/* 404页面 */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </BrowserRouter>
  );
};

export default AppRoutes;
