/**
 * ÁôªÂΩïÁªÑ‰ª∂
 * ‰ΩøÁî®ÂéüÁîüHTML inputÂÖÉÁ¥†ÂíåÂÆåÂÖ®ÂÜÖËÅîÊ†∑Âºè
 */

import React, { useState } from 'react';
import { message } from 'antd';
import { UserOutlined, LockOutlined, EyeOutlined, EyeInvisibleOutlined } from '@ant-design/icons';
import { useAuth } from '../../contexts/AuthContext';

interface LoginFormData {
  username: string;
  password: string;
  remember?: boolean;
}

interface LoginProps {
  onSwitchToRegister: () => void;
  onLoginSuccess?: () => void;
}

// ËæìÂÖ•Ê°ÜÁªÑ‰ª∂
const CustomInput: React.FC<{
  type: string;
  placeholder: string;
  value: string;
  onChange: (value: string) => void;
  icon: React.ReactNode;
  showPasswordToggle?: boolean;
  onTogglePassword?: () => void;
  showPassword?: boolean;
}> = ({ type, placeholder, value, onChange, icon, showPasswordToggle, onTogglePassword, showPassword }) => {
  const [isFocused, setIsFocused] = useState(false);

  // Ê∑ªÂä†Ë∞ÉËØïÊó•ÂøóÂπ∂Â§ÑÁêÜËá™Âä®Â°´ÂÖÖÊ†∑Âºè
  React.useEffect(() => {
    console.log('CustomInput mounted with props:', { type, placeholder });

    // Â§ÑÁêÜËá™Âä®Â°´ÂÖÖÊ†∑ÂºèÈóÆÈ¢ò
    const handleAutofill = () => {
      const input = document.querySelector('.login-input') as HTMLInputElement;
      if (input) {
        // Âº∫Âà∂Â∫îÁî®Ëá™ÂÆö‰πâÊ†∑Âºè‰ª•Ë¶ÜÁõñÊµèËßàÂô®Ëá™Âä®Â°´ÂÖÖÊ†∑Âºè
        input.style.backgroundColor = 'rgba(255, 255, 255, 0.05)';
        input.style.color = 'white';
        input.style.webkitTextFillColor = 'white';
        input.style.webkitBoxShadow = '0 0 0px 1000px rgba(255, 255, 255, 0.05) inset';
      }
    };

    // ÁõëÂê¨Ëá™Âä®Â°´ÂÖÖ‰∫ã‰ª∂
    const input = document.querySelector('.login-input') as HTMLInputElement;
    if (input) {
      input.addEventListener('animationstart', (e) => {
        if (e.animationName.includes('autofill')) {
          handleAutofill();
        }
      });

      // ÂÆöÊúüÊ£ÄÊü•Ëá™Âä®Â°´ÂÖÖÁä∂ÊÄÅ
      const interval = setInterval(handleAutofill, 1000);
      return () => clearInterval(interval);
    }
  }, [type, placeholder]);

  const handleFocus = () => {
    setIsFocused(true);
    console.log('Input focused, style:', inputStyle);
  };

  const handleBlur = () => {
    setIsFocused(false);
    console.log('Input blurred, value:', value);
  };

  const containerStyle: React.CSSProperties = {
    position: 'relative',
    width: '100%',
    marginBottom: '24px',
  };

  const inputStyle: React.CSSProperties = {
    width: '100%',
