/**
 * Ê≥®ÂÜåÈ°µÈù¢
 * ‰ΩøÁî®AuthLayoutÂ∏ÉÂ±Ä
 */

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import authService from '../services/authService';
import Toast from '../components/Toast';
import '../styles/AuthLayout.css';

const RegisterPage: React.FC = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState<string>('');
  const [password, setPassword] = useState<string>('');
  const [confirmPassword, setConfirmPassword] = useState<string>('');
  const [email, setEmail] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(false);
  const [showPassword, setShowPassword] = useState<boolean>(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState<boolean>(false);
  const [agreeTerms, setAgreeTerms] = useState<boolean>(false);

  // ToastÁä∂ÊÄÅ
  const [showToast, setShowToast] = useState<boolean>(false);
  const [toastMessage, setToastMessage] = useState<string>('');
  const [toastType, setToastType] = useState<'success' | 'error' | 'info'>('info');

  // Â§ÑÁêÜË°®ÂçïÊèê‰∫§
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!username.trim()) {
      alert('ËØ∑ËæìÂÖ•Áî®Êà∑Âêç');
      return;
    }

    if (!email.trim()) {
      alert('ËØ∑ËæìÂÖ•ÈÇÆÁÆ±');
      return;
    }

    if (!password.trim()) {
      alert('ËØ∑ËæìÂÖ•ÂØÜÁ†Å');
      return;
    }

    if (password !== confirmPassword) {
      alert('‰∏§Ê¨°ËæìÂÖ•ÁöÑÂØÜÁ†Å‰∏ç‰∏ÄËá¥');
      return;
    }

    if (!agreeTerms) {
      alert('ËØ∑ÈòÖËØªÂπ∂ÂêåÊÑèÁî®Êà∑ÂçèËÆÆ');
      return;
    }

    setLoading(true);

    try {
      // ÂÆûÈôÖÊ≥®ÂÜåÈÄªËæë
      const success = await register(username, email, password);
      if (success) {
        // Ê≥®ÂÜåÊàêÂäüÂêéÈáçÂÆöÂêëÂà∞‰ª™Ë°®Áõò
        navigate('/dashboard');
      } else {
        setLoading(false);
      }
    } catch (error: any) {
      setLoading(false);
      alert(error.message || 'Ê≥®ÂÜåÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
    }
  };

  // Â§ÑÁêÜÂêåÊÑèÊù°Ê¨æÂ§çÈÄâÊ°ÜÁÇπÂáª
  const handleAgreeTermsClick = () => {
    setAgreeTerms(!agreeTerms);
  };

  // Â§ÑÁêÜÁ§æ‰∫§ÁôªÂΩïÊåâÈíÆÁÇπÂáª
  const handleSocialLoginClick = (e: React.MouseEvent, platform: string) => {
    e.preventDefault();
    alert(`${platform}ÂäüËÉΩÂºÄÂèë‰∏≠ÔºåÊï¨ËØ∑ÊúüÂæÖÔºÅ`);
  };

  // Â§ÑÁêÜÁôªÂΩïÈìæÊé•ÁÇπÂáª
  const handleLoginClick = (e: React.MouseEvent) => {
    e.preventDefault();
    navigate('/login');
  };

  return (
    <>
      <div className="register-header">
        <div className="register-title">ÂàõÂª∫Ë¥¶Êà∑</div>
        <div className="register-subtitle">Âä†ÂÖ•Êàë‰ª¨ÔºåÂºÄÂßãÊÇ®ÁöÑÊäïËµÑ‰πãÊóÖ</div>
      </div>

      <form id="registerForm" onSubmit={handleSubmit}>
        <div className="form-group">
          <label className="form-label">Áî®Êà∑Âêç</label>
          <div className="input-wrapper">
            <input 
              type="text" 
              className="form-input" 
              placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑Âêç" 
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              autoComplete="new-password"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.06)' }}
            />
            <div className="input-icon">üë§</div>
          </div>
        </div>

        <div className="form-group">
          <label className="form-label">ÈÇÆÁÆ±</label>
          <div className="input-wrapper">
            <input 
              type="email" 
              className="form-input" 
              placeholder="ËØ∑ËæìÂÖ•ÈÇÆÁÆ±" 
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              autoComplete="new-password"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.06)' }}
            />
            <div className="input-icon">üìß</div>
          </div>
        </div>

        <div className="form-group">
          <label className="form-label">ÂØÜÁ†Å</label>
          <div className="input-wrapper">
            <input 
              type={showPassword ? "text" : "password"} 
              className="form-input" 
              placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              autoComplete="new-password"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.06)' }}
            />
            <div 
              className="input-icon password-toggle" 
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? 'üëÅÔ∏è' : 'üîí'}
            </div>
          </div>
        </div>

        <div className="form-group">
          <label className="form-label">Á°ÆËÆ§ÂØÜÁ†Å</label>
          <div className="input-wrapper">
            <input 
              type={showConfirmPassword ? "text" : "password"} 
              className="form-input" 
              placeholder="ËØ∑ÂÜçÊ¨°ËæìÂÖ•ÂØÜÁ†Å" 
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
              autoComplete="new-password"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.06)' }}
            />
            <div 
              className="input-icon password-toggle" 
              onClick={() => setShowConfirmPassword(!showConfirmPassword)}
            >
              {showConfirmPassword ? 'üëÅÔ∏è' : 'üîí'}
            </div>
          </div>
        </div>

        <div className="form-options">
          <label className="agree-terms" onClick={handleAgreeTermsClick}>
            <div className={`custom-checkbox ${agreeTerms ? 'checked' : ''}`}></div>
            ÊàëÂ∑≤ÈòÖËØªÂπ∂ÂêåÊÑè<button type="button" className="terms-link">Áî®Êà∑ÂçèËÆÆ</button>Âíå<button type="button" className="privacy-link">ÈöêÁßÅÊîøÁ≠ñ</button>
          </label>
        </div>

        <button type="submit" className="register-button" disabled={loading}>
          {loading ? (
            <>
              <div className="loading"></div>
              <span>Ê≥®ÂÜå‰∏≠...</span>
            </>
          ) : (
            <span className="button-text">Ê≥®ÂÜå</span>
          )}
        </button>
      </form>

      <div className="social-login">
        <div className="divider">
          <span>Êàñ‰ΩøÁî®‰ª•‰∏ãÊñπÂºèÊ≥®ÂÜå</span>
        </div>
        <div className="social-buttons">
          <button 
