/**
 * 股票数据看板页面
 * 完整的股票数据展示和管理功能
 */

import React, { useState, useEffect, useCallback } from 'react';
import { Layout, message, Spin, Button, Space, Typography } from 'antd';
import { ReloadOutlined, PlusOutlined } from '@ant-design/icons';
import { StockInfo, apiService } from '../services/api';
import SearchBox from '../components/SearchBox';
import StatsCards from '../components/StatsCards';
import StockTable from '../components/StockTable';
import '../styles/DashboardPage.css';

const { Content } = Layout;
const { Title } = Typography;

const DashboardPage: React.FC = () => {
  const [stocks, setStocks] = useState<StockInfo[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [refreshing, setRefreshing] = useState<boolean>(false);
  const [drawerOpen, setDrawerOpen] = useState<boolean>(false);

  // 获取关注列表
  const fetchWatchlist = useCallback(async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const response = await apiService.getWatchlist();
      if (response.success && response.data) {
        setStocks(response.data);
      } else {
        message.error(response.error || '获取关注列表失败');
      }
    } catch (error) {
      console.error('获取关注列表失败:', error);
      message.error('获取关注列表失败，请重试');
    } finally {
      if (showLoading) setLoading(false);
    }
  }, []);

  // 刷新数据
  const handleRefresh = async () => {
    setRefreshing(true);
    try {
      await fetchWatchlist(false);
      message.success('数据已刷新');
    } catch (error) {
      message.error('刷新失败');
    } finally {
      setRefreshing(false);
    }
  };

  // 添加股票到关注列表
  const handleAddStock = async (stock: { code: string; name: string; industry?: string }) => {
    try {
      const response = await apiService.addToWatchlist(stock.code, stock.industry || '');
      if (response.success) {
        message.success(`已添加 ${stock.name} 到关注列表`);
        await fetchWatchlist(false); // 刷新列表
      } else {
        message.error(response.error || '添加失败');
      }
    } catch (error) {
      console.error('添加股票失败:', error);
      message.error('添加失败，请重试');
    }
  };

  // 从关注列表移除股票
  const handleRemoveStock = async (code: string) => {
    try {
      const response = await apiService.removeFromWatchlist(code);
      if (response.success) {
        message.success('已从关注列表移除');
        await fetchWatchlist(false); // 刷新列表
      } else {
        message.error(response.error || '移除失败');
      }
    } catch (error) {
      console.error('移除股票失败:', error);
      message.error('移除失败，请重试');
    }
  };

  // 处理抽屉状态变化
  const handleDrawerStateChange = (isOpen: boolean) => {
    setDrawerOpen(isOpen);
  };

  // 初始化加载数据
  useEffect(() => {
    fetchWatchlist();
  }, [fetchWatchlist]);

  return (
    <Content style={{ 
      padding: '0', /* 完全移除padding */
      margin: '0', /* 完全移除margin */
      minHeight: 'calc(100vh - 64px - 40px)', /* 减去header和footer高度 */
      background: '#1a1d29', /* 修复背景色为原来的深蓝灰色 */
      display: 'flex',
      flexDirection: 'column'
    }}>
      <div style={{ 
        maxWidth: '1400px', 
        margin: '0 auto',
        width: '100%',
        flex: 1,
        display: 'flex',
        flexDirection: 'column'
      }}>
        {/* 页面标题和操作栏 */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center', 
          padding: '8px 24px',
          paddingTop: '8px',
          marginBottom: '0' // 移除底部margin
        }}>
          <h1 style={{ 
            margin: 0, 
            fontSize: '24px', 
            fontWeight: 'bold', 
            color: '#ffffff' 
          }}>
            股票数据看板
          </h1>
          <Button 
            type="primary" 
            icon={<ReloadOutlined />}
            onClick={handleRefresh}
            loading={refreshing}
            style={{
              background: '#667eea',
              borderColor: '#667eea',
              borderRadius: '6px'
            }}
          >
            刷新数据
          </Button>
        </div>

        {/* 搜索框 */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          padding: '8px 24px', // 与标题栏保持一致的padding
          paddingTop: '8px'
        }}>
          <SearchBox onSelectStock={handleAddStock} />
        </div>
        {!drawerOpen && (
          <div style={{ 
            marginBottom: '12px',
            display: 'flex',
            justifyContent: 'center', /* 恢复居中对齐 */
            position: 'relative',
            padding: '0 24px' /* 与标题使用相同的左右padding，确保垂直对齐 */
          }}>
            <SearchBox 
              onSelectStock={handleAddStock}
              placeholder="搜索股票代码或名称，点击添加到关注列表"
            />
          </div>
        )}

        {/* 统计卡片 */}
        <div style={{ 
          marginBottom: '16px',
          padding: '0 24px'
        }}>
          <StatsCards stocks={stocks} loading={loading} />
        </div>

        {/* 股票列表 */}
        <div style={{ 
          background: 'rgba(255, 255, 255, 0.02)',
          borderRadius: '12px',
          padding: '20px',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          flex: 1,
          display: 'flex',
          flexDirection: 'column',
          margin: '0 24px 24px 24px' /* 左右和底部margin */
        }}>
          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            marginBottom: '16px'
          }}>
            <Title level={4} style={{ margin: 0, color: '#ffffff' }}>
              我的关注列表
            </Title>
            <span style={{ color: '#888', fontSize: '14px' }}>
              API调用: {loading ? '加载中...' : `共 ${stocks.length} 只股票`}
            </span>
          </div>

          <div style={{ flex: 1 }}>
            {loading ? (
              <div style={{ 
                display: 'flex', 
                justifyContent: 'center', 
                alignItems: 'center',
                height: '200px'
              }}>
                <Spin size="large" />
              </div>
            ) : (
              <StockTable 
                stocks={stocks}
                loading={refreshing}
                onRemoveStock={handleRemoveStock}
                onDrawerStateChange={handleDrawerStateChange}
                onRefresh={fetchWatchlist}
              />
            )}
          </div>
        </div>
      </div>
    </Content>
  );
};

export default DashboardPage;
