import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import DashboardPage from './pages/DashboardPage';
import HomePage from './pages/HomePage';
import ConfirmModal from './components/ConfirmModal';
import Toast from './components/Toast';
import { authService } from './services/authService';
import { tokenManager } from './services/api';
import './App.css';

/**
 * App组件 - 应用入口点
 * 使用最简单的认证逻辑，直接在App组件中处理
 */
const App: React.FC = () => {
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [userInfo, setUserInfo] = useState<{ username: string; email?: string } | null>(null);
  
  // 弹窗状态
  const [showLogoutConfirm, setShowLogoutConfirm] = useState<boolean>(false);
  const [showToast, setShowToast] = useState<boolean>(false);
  const [toastMessage, setToastMessage] = useState<string>('');

  useEffect(() => {
    const checkAuthStatus = async () => {
      console.log('=== App 检查认证状态 ===');
      
      // 检查本地存储中的登录状态
      const token = localStorage.getItem('auth_token');
      const storedUserInfo = localStorage.getItem('userInfo');
      
      console.log('本地token:', token ? '存在' : '不存在');
      console.log('本地用户信息:', storedUserInfo ? '存在' : '不存在');
      
      if (token) {
        try {
          // 验证token是否有效
          console.log('验证token有效性...');
          const verifyResult = await authService.verifyToken();
          
          if (verifyResult.success && verifyResult.user) {
            console.log('Token验证成功，用户已认证');
            setIsLoggedIn(true);
            setUserInfo(verifyResult.user);
          } else {
            console.log('Token验证失败，清除认证状态');
            // Token无效，清除本地存储
            tokenManager.removeToken();
            localStorage.removeItem('userInfo');
            setIsLoggedIn(false);
            setUserInfo(null);
          }
        } catch (error) {
          console.error('Token验证异常:', error);
          // 验证失败，清除本地存储
          tokenManager.removeToken();
          localStorage.removeItem('userInfo');
          setIsLoggedIn(false);
          setUserInfo(null);
        }
      } else {
        console.log('没有token，用户未认证');
        setIsLoggedIn(false);
        setUserInfo(null);
      }
      
      setIsLoading(false);
    };

    checkAuthStatus();
  }, []);

  const handleLogin = (loginStatus: boolean) => {
    setIsLoggedIn(loginStatus);
    if (loginStatus) {
      // 登录成功时，重新读取用户信息
      const storedUserInfo = localStorage.getItem('userInfo');
      if (storedUserInfo) {
        try {
          setUserInfo(JSON.parse(storedUserInfo));
        } catch (error) {
          console.error('解析用户信息失败:', error);
        }
      }
    } else {
      // 登出时清除用户信息
      setUserInfo(null);
    }
  };

  const handleLogout = () => {
    setShowLogoutConfirm(true);
  };

  const confirmLogout = () => {
    localStorage.removeItem('auth_token');
    localStorage.removeItem('userInfo');
    handleLogin(false); // 使用handleLogin函数来更新状态
    setShowLogoutConfirm(false);
    setToastMessage('已成功退出登录');
    setShowToast(true);
  };

  const cancelLogout = () => {
    setShowLogoutConfirm(false);
  };

  if (isLoading) {
    return <div style={{ 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      height: '100vh',
      fontSize: '18px'
    }}>正在检查登录状态...</div>;
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route
            path="/"
            element={<HomePage isLoggedIn={isLoggedIn} userInfo={userInfo || undefined} onLogout={handleLogout} />}
          />
          <Route
            path="/login"
            element={
              isLoggedIn ? (
                <Navigate to="/dashboard" replace />
              ) : (
                <LoginPage onLogin={handleLogin} />
              )
            }
          />
          <Route
            path="/register"
            element={
              isLoggedIn ? (
                <Navigate to="/dashboard" replace />
              ) : (
                <RegisterPage />
              )
            }
          />
          <Route
            path="/dashboard"
            element={
              isLoggedIn ? (
                <DashboardPage onLogout={handleLogout} />
              ) : (
                <Navigate to="/login" replace />
              )
            }
          />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>

        {/* 确认退出登录弹窗 */}
        <ConfirmModal
          isOpen={showLogoutConfirm}
          title="确认退出"
          message="您确定要退出登录吗？退出后需要重新登录才能访问仪表盘。"
          confirmText="退出登录"
          cancelText="取消"
          onConfirm={confirmLogout}
          onCancel={cancelLogout}
          type="warning"
        />

        {/* Toast 提示 */}
        <Toast
          isVisible={showToast}
          message={toastMessage}
          type="success"
          onClose={() => setShowToast(false)}
        />
      </div>
    </Router>
  );
};

export default App;
