export const Login: React.FC<LoginProps> = ({ onSwitchToRegister, onLoginSuccess }) => {
  const [formData, setFormData] = useState<LoginFormData>({
    username: '',
    password: '',
  });

  const [errors, setErrors] = useState<Partial<LoginFormData>>({});
  const [isLoading, setIsLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);

  // 简单的聚焦状态
  const [focusedField, setFocusedField] = useState<string | null>(null);

  const { login } = useAuth();

  const validateForm = (): boolean => {
    const newErrors: Partial<LoginFormData> = {};

    if (!formData.username.trim()) {
      newErrors.username = '请输入用户名或邮箱';
    } else if (formData.username.length < 3) {
      newErrors.username = '用户名至少需要3个字符';
    }

    if (!formData.password) {
      newErrors.password = '请输入密码';
    } else if (formData.password.length < 6) {
      newErrors.password = '密码至少需要6个字符';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsLoading(true);

    try {
      const success = await login(formData.username, formData.password);
      if (success) {
        onLoginSuccess?.();
      }
    } catch (error) {
      message.error('登录失败，请重试');
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (field: keyof LoginFormData, value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: undefined }));
    }
  };

  const getInputStyle = (field: 'username' | 'password') => {
    const baseStyle = {
      ...styles.input,
      ...(focusedField === field ? styles.inputFocus : {}),
      ...(errors[field] ? { borderColor: '#ef4444' } : {}),
    };

    // 为密码输入框添加右侧 padding，为切换按钮留出空间
    if (field === 'password') {
      baseStyle.paddingRight = '50px';
    }

    return baseStyle;
  };

  const handleWechatLogin = () => {
    message.info('微信登录功能开发中，敬请期待！');
  };

  const handleForgotPassword = () => {
    message.info('忘记密码功能开发中，敬请期待！');
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.title}>欢迎回来</div>
        <div style={styles.subtitle}>登录您的账户以继续使用</div>

        <form style={styles.form} onSubmit={handleSubmit}>
          {/* 用户名输入框 */}
          <div style={styles.inputGroup}>
            <label style={styles.label}>用户名</label>
            <div style={styles.inputContainer}>
              <div style={styles.inputIcon}>👤</div>
              <input
                type="text"
                placeholder="请输入用户名或邮箱"
                value={formData.username}
                onChange={(e) => handleInputChange('username', e.target.value)}
                onFocus={() => setFocusedField('username')}
                onBlur={() => setFocusedField(null)}
                style={{
                  ...styles.input,
                  ...(focusedField === 'username' ? styles.inputFocus : {}),
                  ...(errors.username ? { borderColor: '#ef4444' } : {}),
                }}
              />
            </div>
            {errors.username && (
              <div style={{ color: '#ef4444', fontSize: '12px', marginTop: '4px' }}>
                {errors.username}
              </div>
            )}
          </div>

          {/* 密码输入框 */}
          <div style={styles.inputGroup}>
            <label style={styles.label}>密码</label>
            <div style={styles.inputContainer}>
              <div style={styles.inputIcon}>🔒</div>
              <input
                type={showPassword ? 'text' : 'password'}
                placeholder="请输入密码"
                value={formData.password}
                onChange={(e) => handleInputChange('password', e.target.value)}
                onFocus={() => setFocusedField('password')}
                onBlur={() => setFocusedField(null)}
                style={{
                  ...styles.input,
                  ...(focusedField === 'password' ? styles.inputFocus : {}),
                  ...(errors.password ? { borderColor: '#ef4444' } : {}),
                }}
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                style={styles.passwordToggle}
              >
                {showPassword ? '🙈' : '👁️'}
              </button>
            </div>
            {errors.password && (
              <div style={{ color: '#ef4444', fontSize: '12px', marginTop: '4px' }}>
                {errors.password}
              </div>
            )}
          </div>

          {/* 记住我和忘记密码 */}
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            fontSize: '14px',
          }}>
            <label style={{
              display: 'flex',
              alignItems: 'center',
              gap: '8px',
              color: 'rgba(255, 255, 255, 0.8)',
              cursor: 'pointer',
            }}>
              <input
                type="checkbox"
                checked={rememberMe}
                onChange={(e) => setRememberMe(e.target.checked)}
                style={{
                  width: '16px',
                  height: '16px',
                  accentColor: '#4096ff',
                }}
              />
              记住我
            </label>
            <button
              type="button"
              style={{
                background: 'none',
                border: 'none',
                color: '#4096ff',
                cursor: 'pointer',
                fontSize: '14px',
                textDecoration: 'underline',
              }}
            >
              忘记密码？
            </button>
          </div>

          {/* 登录按钮 */}
          <button
            type="submit"
            disabled={isLoading}
            style={{
              width: '100%',
              height: '48px',
              background: '#4096ff',
              border: 'none',
              borderRadius: '12px',
              color: 'white',
              fontSize: '16px',
              fontWeight: '600',
              cursor: isLoading ? 'not-allowed' : 'pointer',
              opacity: isLoading ? 0.7 : 1,
              transition: 'all 0.3s ease',
            }}
          >
            {isLoading ? '登录中...' : '登录'}
          </button>

          {/* 分割线 */}
          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '16px',
            margin: '20px 0',
          }}>
            <div style={{
              flex: 1,
              height: '1px',
              background: 'rgba(255, 255, 255, 0.1)',
            }}></div>
            <span style={{
              color: 'rgba(255, 255, 255, 0.5)',
              fontSize: '14px',
            }}>
              或使用以下方式登录
            </span>
            <div style={{
              flex: 1,
              height: '1px',
              background: 'rgba(255, 255, 255, 0.1)',
            }}></div>
          </div>

          {/* 微信登录 */}
          <button
            type="button"
            style={{
              width: '100%',
              height: '48px',
              background: 'rgba(255, 255, 255, 0.05)',
              border: '1px solid rgba(255, 255, 255, 0.1)',
              borderRadius: '12px',
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '16px',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '8px',
              transition: 'all 0.3s ease',
            }}
          >
            💬 微信登录
          </button>

          {/* 注册链接 */}
          <div style={{
            textAlign: 'center',
            marginTop: '20px',
            fontSize: '14px',
            color: 'rgba(255, 255, 255, 0.7)',
          }}>
            还没有账号？{' '}
            <button
              type="button"
              onClick={onSwitchToRegister}
              style={{
                background: 'none',
                border: 'none',
                color: '#4096ff',
                cursor: 'pointer',
                fontSize: '14px',
                textDecoration: 'underline',
              }}
            >
              立即注册
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};
