import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import DashboardPage from './pages/DashboardPage';
import './styles/global.css';

/**
 * App组件 - 应用入口点
 * 使用最简单的认证逻辑，直接在App组件中处理
 */
const App: React.FC = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [userInfo, setUserInfo] = useState<{username: string; email?: string} | null>(null);

  useEffect(() => {
    // 页面加载时检查token
    const token = localStorage.getItem('userToken');
    const savedUserInfo = localStorage.getItem('userInfo');
    console.log('App初始化，检查token:', token);
    
    if (token) {
      setIsLoggedIn(true);
      if (savedUserInfo) {
        try {
          setUserInfo(JSON.parse(savedUserInfo));
        } catch (error) {
          console.error('解析用户信息失败:', error);
        }
      }
      console.log('发现token，设置为已登录');
    } else {
      setIsLoggedIn(false);
      setUserInfo(null);
      console.log('没有token，设置为未登录');
    }
    setIsLoading(false);
  }, []);

  // 退出登录处理函数
  const handleLogout = () => {
    localStorage.removeItem('userToken');
    localStorage.removeItem('userInfo');
    setIsLoggedIn(false);
    setUserInfo(null);
    console.log('已清除token和用户信息，设置为未登录');
  };

  if (isLoading) {
    return <div style={{ 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      height: '100vh',
      fontSize: '18px'
    }}>正在检查登录状态...</div>;
  }

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={isLoggedIn ? <Navigate to="/dashboard" /> : <LoginPage setIsLoggedIn={setIsLoggedIn} />}
        />
        <Route
          path="/dashboard"
          element={isLoggedIn ? <DashboardPage onLogout={handleLogout} /> : <Navigate to="/login" />}
        />
        <Route path="/" element={<Navigate to={isLoggedIn ? "/dashboard" : "/login"} />} />
        <Route path="*" element={<Navigate to={isLoggedIn ? "/dashboard" : "/login"} />} />
      </Routes>
    </Router>
  );
};

export default App;
