import React, { useState } from 'react';
import { Button, Card, Space, Typography, message, Collapse, Input, Spin } from 'antd';
import { PlayCircleOutlined, StopOutlined, CodeOutlined, GlobalOutlined } from '@ant-design/icons';

const { Title, Paragraph, Text } = Typography;
const { Panel } = Collapse;
const { TextArea } = Input;

interface MCPServiceProps {
  title?: string;
}

const MCPService: React.FC<MCPServiceProps> = ({ title = '浏览器自动化 MCP 服务' }) => {
  const [status, setStatus] = useState<'未启动' | '正在启动...' | '已启动' | '已停止' | '启动失败'>('未启动');
  const [output, setOutput] = useState('');
  const [loading, setLoading] = useState(false);
  const [url, setUrl] = useState('https://www.google.com');
  const [jsCode, setJsCode] = useState('document.title');
  const [scriptResult, setScriptResult] = useState('');

  // 模拟启动MCP服务器
  const startMCPServer = async () => {
    try {
      setLoading(true);
      setStatus('正在启动...');

      // 模拟API调用延迟
      await new Promise(resolve => setTimeout(resolve, 2000));

      // 在实际应用中，你需要通过后端API来启动MCP服务器
      // 例如：await api.post('/api/mcp/start');

      setStatus('已启动');
      setOutput('MCP服务器已成功启动，可以通过AI助手连接使用。\n' + 
                '服务器地址: localhost:8080\n' +
                '支持功能: 浏览器自动化、网页内容读取、截图、表单填写等');
      message.success('MCP服务器已启动');
    } catch (error) {
      setStatus('启动失败');
      setOutput(`错误: ${error instanceof Error ? error.message : String(error)}`);
      message.error('MCP服务器启动失败');
    } finally {
      setLoading(false);
    }
  };

  // 模拟停止MCP服务器
  const stopMCPServer = async () => {
    try {
      setLoading(true);

      // 模拟API调用延迟
      await new Promise(resolve => setTimeout(resolve, 1000));

      // 在实际应用中，你需要通过后端API来停止MCP服务器
      // 例如：await api.post('/api/mcp/stop');

      setStatus('已停止');
      setOutput('MCP服务器已停止');
      message.info('MCP服务器已停止');
    } catch (error) {
      setOutput(`停止服务出错: ${error instanceof Error ? error.message : String(error)}`);
      message.error('MCP服务器停止失败');
    } finally {
      setLoading(false);
    }
  };

  // 模拟浏览器导航
  const navigateTo = async () => {
    if (status !== '已启动') {
      message.warning('请先启动MCP服务器');
      return;
    }

    try {
      setLoading(true);
      setOutput(`正在导航到: ${url}...`);

      // 模拟API调用延迟
      await new Promise(resolve => setTimeout(resolve, 1500));

      // 在实际应用中，你需要通过MCP服务器API来控制浏览器
      // 例如：await api.post('/api/mcp/navigate', { url });

      setOutput(prev => `${prev}\n导航成功！页面已加载完成。`);
      message.success(`已成功导航到 ${url}`);
    } catch (error) {
      setOutput(prev => `${prev}\n导航失败: ${error instanceof Error ? error.message : String(error)}`);
      message.error('导航失败');
    } finally {
      setLoading(false);
    }
  };

  // 模拟执行JavaScript
  const executeScript = async () => {
    if (status !== '已启动') {
      message.warning('请先启动MCP服务器');
      return;
    }

    try {
      setLoading(true);
      setScriptResult('');
      setOutput(`正在执行JavaScript代码...`);

      // 模拟API调用延迟
      await new Promise(resolve => setTimeout(resolve, 1000));

      // 在实际应用中，你需要通过MCP服务器API来执行JavaScript
      // 例如：const result = await api.post('/api/mcp/evaluate', { code: jsCode });

      // 模拟执行结果
      let mockResult;
      if (jsCode.includes('document.title')) {
        mockResult = '"网页标题示例"';
      } else if (jsCode.includes('location')) {
        mockResult = '"https://example.com"';
      } else {
        mockResult = '执行成功，无返回值';
      }

      setScriptResult(mockResult);
      setOutput(prev => `${prev}\nJavaScript执行成功！`);
      message.success('JavaScript执行成功');
    } catch (error) {
      setOutput(prev => `${prev}\n执行失败: ${error instanceof Error ? error.message : String(error)}`);
      message.error('JavaScript执行失败');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Card 
      title={title} 
      style={{ marginBottom: 20 }}
      extra={<Text type={status === '已启动' ? 'success' : 'secondary'}>状态: {status}</Text>}
    >
      <Spin spinning={loading}>
        <Typography>
          <Paragraph>
            MCP (Model Context Protocol) 服务器允许AI助手直接操作浏览器，执行网页浏览、内容抓取和自动化测试等任务。
            通过Playwright实现的MCP服务器，可以让大语言模型与网页进行结构化交互。
          </Paragraph>

          <Space style={{ marginBottom: 16 }}>
            <Button 
              type="primary" 
              icon={<PlayCircleOutlined />}
              onClick={startMCPServer} 
              disabled={status === '已启动' || status === '正在启动...'}
            >
              启动服务
            </Button>
            <Button 
              danger 
              icon={<StopOutlined />}
              onClick={stopMCPServer} 
              disabled={status !== '已启动'}
            >
              停止服务
            </Button>
          </Space>

          {output && (
            <div style={{ marginBottom: 16, padding: 16, background: '#f5f5f5', borderRadius: 4 }}>
              <pre style={{ whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}>{output}</pre>
            </div>
          )}

          <Collapse ghost>
            <Panel header="浏览器操作" key="1">
              <Space direction="vertical" style={{ width: '100%' }}>
                <Input 
                  addonBefore={<GlobalOutlined />}
                  placeholder="输入URL" 
                  value={url} 
                  onChange={e => setUrl(e.target.value)} 
                />
                <Button 
                  type="primary" 
                  onClick={navigateTo} 
                  disabled={status !== '已启动'}
                  style={{ marginTop: 8 }}
                >
                  导航到URL
                </Button>
              </Space>
            </Panel>

            <Panel header="执行JavaScript" key="2">
              <Space direction="vertical" style={{ width: '100%' }}>
                <TextArea 
                  rows={4} 
                  value={jsCode} 
                  onChange={e => setJsCode(e.target.value)} 
                  placeholder="输入JavaScript代码"
                />
                <Button 
                  type="primary" 
                  icon={<CodeOutlined />}
                  onClick={executeScript} 
                  disabled={status !== '已启动'}
                >
                  执行代码
                </Button>
                {scriptResult && (
                  <div style={{ padding: 8, background: '#f0f0f0', borderRadius: 4 }}>
                    <Text strong>执行结果:</Text>
                    <pre style={{ marginTop: 8 }}>{scriptResult}</pre>
                  </div>
                )}
              </Space>
            </Panel>
          </Collapse>
        </Typography>
      </Spin>
    </Card>
  );
};

export default MCPService;
