import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import './Login.css';

interface LoginProps {
  onSwitchToRegister?: () => void;
  onLoginSuccess?: () => void;
}

export const Login: React.FC<LoginProps> = ({ onSwitchToRegister, onLoginSuccess }) => {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [username, setUsername] = useState('');

  // ÁªÑ‰ª∂Âä†ËΩΩÊó∂Ê£ÄÊü•localStorage‰∏≠ÊòØÂê¶Êúâ‰øùÂ≠òÁöÑÁî®Êà∑Âêç
  useEffect(() => {
    const rememberedUsername = localStorage.getItem('rememberedUsername');
    if (rememberedUsername) {
      setUsername(rememberedUsername);
      setRememberMe(true);
    }
  }, []);
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  // Â§ÑÁêÜË°®ÂçïÊèê‰∫§
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setErrorMessage('');

    if (!username.trim()) {
      setErrorMessage('ËØ∑ËæìÂÖ•Áî®Êà∑Âêç');
      return;
    }

    if (!password.trim()) {
      setErrorMessage('ËØ∑ËæìÂÖ•ÂØÜÁ†Å');
      return;
    }

    setLoading(true);

    try {
      // ÂÆûÈôÖÁôªÂΩïÈÄªËæë
      const success = await login(username, password);
      if (success) {
        console.log('ÁôªÂΩïÊàêÂäüÔºåÂáÜÂ§áË∑≥ËΩ¨...');

        // Â¶ÇÊûúÂãæÈÄâ‰∫Ü"ËÆ∞‰ΩèÊàë"Ôºå‰øùÂ≠òÁî®Êà∑ÂêçÂà∞localStorage
        if (rememberMe) {
          localStorage.setItem('rememberedUsername', username);
        } else {
          localStorage.removeItem('rememberedUsername');
        }

        // Â¶ÇÊûúÊúâÂõûË∞ÉÂáΩÊï∞ÔºåÊâßË°åÂõûË∞É
        if (onLoginSuccess) {
          onLoginSuccess();
        } else {
          // Âê¶ÂàôÁõ¥Êé•Ë∑≥ËΩ¨Âà∞‰ª™Ë°®Áõò
          navigate('/dashboard');
        }
      } else {
        setErrorMessage('ÁôªÂΩïÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•Áî®Êà∑ÂêçÂíåÂØÜÁ†Å');
        setLoading(false);
      }
    } catch (error: any) {
      setLoading(false);
      setErrorMessage(error.message || 'ÁôªÂΩïÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
    }
  };

  // Â§ÑÁêÜËÆ∞‰ΩèÊàëÂ§çÈÄâÊ°ÜÁÇπÂáª
  const handleRememberMeClick = () => {
    setRememberMe(!rememberMe);
  };

  // Â§ÑÁêÜÂøòËÆ∞ÂØÜÁ†ÅÁÇπÂáª
  const handleForgotPasswordClick = (e: React.MouseEvent) => {
    e.preventDefault();
    alert('ÂøòËÆ∞ÂØÜÁ†ÅÂäüËÉΩÂºÄÂèë‰∏≠ÔºåËØ∑ËÅîÁ≥ªÁÆ°ÁêÜÂëòÔºÅ');
  };

  // Â§ÑÁêÜÁ§æ‰∫§ÁôªÂΩïÊåâÈíÆÁÇπÂáª
  const handleSocialLoginClick = (e: React.MouseEvent, platform: string) => {
    e.preventDefault();
    alert(`${platform}ÂäüËÉΩÂºÄÂèë‰∏≠ÔºåÊï¨ËØ∑ÊúüÂæÖÔºÅ`);
  };

  // Â§ÑÁêÜÊ≥®ÂÜåÈìæÊé•ÁÇπÂáª
  const handleRegisterClick = (e: React.MouseEvent) => {
    e.preventDefault();
    if (onSwitchToRegister) {
      onSwitchToRegister();
    } else {
      alert('Ê≥®ÂÜåÂäüËÉΩÂºÄÂèë‰∏≠ÔºåÊï¨ËØ∑ÊúüÂæÖÔºÅ');
    }
  };

  return (
    <>
      <div className="login-header">
        <div className="login-title">Ê¨¢ËøéÂõûÊù•</div>
        <div className="login-subtitle">ÁôªÂΩïÊÇ®ÁöÑË¥¶Êà∑‰ª•ÁªßÁª≠‰ΩøÁî®</div>
      </div>

      {errorMessage && (
        <div className="error-message">
          {errorMessage}
        </div>
      )}

      <form id="loginForm" onSubmit={handleSubmit}>
        <div className="form-group">
          <label className="form-label">Áî®Êà∑Âêç</label>
          <div className="input-wrapper">
            <input 
              type="text" 
              className="form-input" 
              placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑Âêç" 
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              autoComplete="new-password"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.06)' }}
            />
            <div className="input-icon">üë§</div>
          </div>
        </div>

        <div className="form-group">
          <label className="form-label">ÂØÜÁ†Å</label>
          <div className="input-wrapper">
            <input 
              type={showPassword ? "text" : "password"} 
              className="form-input" 
              placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              autoComplete="new-password"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.06)' }}
            />
            <div 
              className="input-icon password-toggle" 
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? 'üëÅÔ∏è' : 'üîí'}
            </div>
          </div>
        </div>

        <div className="form-options">
          <label className="remember-me" onClick={handleRememberMeClick}>
            <div className={`custom-checkbox ${rememberMe ? 'checked' : ''}`}></div>
            ËÆ∞‰ΩèÊàë
          </label>
          <button type="button" className="forgot-password" onClick={handleForgotPasswordClick}>ÂøòËÆ∞ÂØÜÁ†ÅÔºü</button>
        </div>

        <button type="submit" className="login-button" disabled={loading}>
          {loading ? (
            <>
              <div className="loading"></div>
              <span>ÁôªÂΩï‰∏≠...</span>
            </>
          ) : (
            <span className="button-text">ÁôªÂΩï</span>
          )}
        </button>
      </form>

      <div className="social-login">
        <div className="divider">
          <span>Êàñ‰ΩøÁî®‰ª•‰∏ãÊñπÂºèÁôªÂΩï</span>
        </div>
        <div className="social-buttons">
          <button 
            type="button" 
            className="social-button"
            onClick={(e) => handleSocialLoginClick(e, 'ÂæÆ‰ø°ÁôªÂΩï')}
          >
            <span>üí¨</span>
            ÂæÆ‰ø°ÁôªÂΩï
          </button>
          <button 
            type="button" 
            className="social-button"
            onClick={(e) => handleSocialLoginClick(e, 'ÊîØ‰ªòÂÆùÁôªÂΩï')}
          >
            <span>üì±</span>
            ÊîØ‰ªòÂÆùÁôªÂΩï
          </button>
        </div>
      </div>

      <div className="register-link">
        ËøòÊ≤°ÊúâË¥¶Âè∑Ôºü<button type="button" className="register-button" onClick={handleRegisterClick}>Á´ãÂç≥Ê≥®ÂÜå</button>
      </div>
    </>
  );
};

export default Login;
