export const Login: React.FC<LoginProps> = ({ onSwitchToRegister, onLoginSuccess }) => {
  const [formData, setFormData] = useState<LoginFormData>({
    username: '',
    password: '',
  });

  const [errors, setErrors] = useState<Partial<LoginFormData>>({});
  const [isLoading, setIsLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const [buttonHovered, setButtonHovered] = useState(false);

  // 简单的聚焦状态
  const [focusedField, setFocusedField] = useState<string | null>(null);

  const { login } = useAuth();

  const validateForm = (): boolean => {
    const newErrors: Partial<LoginFormData> = {};

    if (!formData.username.trim()) {
      newErrors.username = '请输入用户名或邮箱';
    } else if (formData.username.length < 3) {
      newErrors.username = '用户名至少需要3个字符';
    }

    if (!formData.password) {
      newErrors.password = '请输入密码';
    } else if (formData.password.length < 6) {
      newErrors.password = '密码至少需要6个字符';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsLoading(true);

    try {
      const success = await login(formData.username, formData.password);
      if (success) {
        onLoginSuccess?.();
      }
    } catch (error) {
      message.error('登录失败，请重试');
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (field: keyof LoginFormData, value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: undefined }));
    }
  };

  const getInputStyle = (field: 'username' | 'password') => {
    const baseStyle = {
      ...styles.input,
      ...(focusedField === field ? styles.inputFocus : {}),
      ...(errors[field] ? { borderColor: '#ef4444' } : {}),
    };

    // 为密码输入框添加右侧 padding，为切换按钮留出空间
    if (field === 'password') {
      baseStyle.paddingRight = '50px';
    }

    return baseStyle;
  };

  const handleWechatLogin = () => {
    message.info('微信登录功能开发中，敬请期待！');
  };

  const handleForgotPassword = () => {
    message.info('忘记密码功能开发中，敬请期待！');
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.title}>欢迎回来</div>
        <div style={styles.subtitle}>登录您的账户以继续使用</div>

        <form style={styles.form} onSubmit={handleSubmit}>
          <div style={styles.inputGroup}>
            <label style={styles.label}>用户名</label>
            <div style={styles.inputContainer}>
              <UserOutlined style={styles.inputIcon} />
              <input
                type="text"
                placeholder="请输入用户名或邮箱"
                value={formData.username}
                onChange={(e) => handleInputChange('username', e.target.value)}
                onFocus={() => setFocusedField('username')}
                onBlur={() => setFocusedField(null)}
                style={getInputStyle('username')}
              />
            </div>
            {errors.username && <div style={styles.errorMessage}>{errors.username}</div>}
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>密码</label>
            <div style={styles.inputContainer}>
              <LockOutlined style={styles.inputIcon} />
              <input
                type={showPassword ? 'text' : 'password'}
                placeholder="请输入密码"
                value={formData.password}
                onChange={(e) => handleInputChange('password', e.target.value)}
                onFocus={() => setFocusedField('password')}
                onBlur={() => setFocusedField(null)}
                style={getInputStyle('password')}
              />
              <button
                type="button"
                style={styles.passwordToggle}
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? '🙈' : '👁️'}
              </button>
            </div>
            {errors.password && <div style={styles.errorMessage}>{errors.password}</div>}
          </div>

          <div style={styles.formOptions}>
            <div style={styles.checkboxWrapper}>
              <input
                type="checkbox"
                id="remember"
                checked={rememberMe}
                onChange={(e) => setRememberMe(e.target.checked)}
                style={styles.checkbox}
              />
              <label htmlFor="remember" style={styles.checkboxLabel}>记住我</label>
            </div>
            <button
              type="button"
              style={styles.forgotPassword}
              onClick={handleForgotPassword}
            >
              忘记密码？
            </button>
          </div>

          <button
            type="submit"
            style={{
              ...styles.loginButton,
              ...(buttonHovered && !isLoading ? styles.loginButtonHover : {}),
              ...(isLoading ? styles.loginButtonDisabled : {}),
            }}
            onMouseEnter={() => setButtonHovered(true)}
            onMouseLeave={() => setButtonHovered(false)}
            disabled={isLoading}
          >
            {isLoading ? '登录中...' : '登录'}
          </button>
        </form>

        <div style={styles.socialLogin}>
          <div style={styles.divider}>
            <span>或使用以下方式登录</span>
          </div>
          <button style={styles.wechatButton} onClick={handleWechatLogin}>
            <WechatOutlined />
            微信登录
          </button>
        </div>

        <div style={styles.registerLink}>
          还没有账号？
          <button style={styles.registerButton} onClick={onSwitchToRegister}>
            立即注册
          </button>
        </div>
      </div>
    </div>
  );
};
