/**
 * 登录组件
 * 提供用户登录界面 - 使用内联样式确保样式生效
 */

import React, { useState } from 'react';
import { Form, Input, Button, Checkbox, message } from 'antd';
import { UserOutlined, LockOutlined, WechatOutlined } from '@ant-design/icons';
import { useAuth } from '../../contexts/AuthContext';

interface LoginFormData {
  username: string;
  password: string;
  remember?: boolean;
}

interface LoginProps {
  onSwitchToRegister: () => void;
  onLoginSuccess?: () => void;
}

// 内联样式定义
const styles = {
  container: {
    width: '100%',
    maxWidth: '400px',
    margin: '0 auto',
    padding: '0 20px',
  } as React.CSSProperties,

  header: {
    textAlign: 'center' as const,
    marginBottom: '32px',
  } as React.CSSProperties,

  title: {
    fontSize: '28px',
    fontWeight: 'bold',
    color: '#ffffff',
    marginBottom: '8px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  subtitle: {
    fontSize: '16px',
    color: 'rgba(255, 255, 255, 0.7)',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  formGroup: {
    marginBottom: '24px',
  } as React.CSSProperties,

  label: {
    display: 'block',
    fontSize: '14px',
    color: '#ffffff',
    marginBottom: '8px',
    fontWeight: '500',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  input: {
    width: '100%',
    height: '48px',
    padding: '12px 16px',
    fontSize: '16px',
    color: '#ffffff',
    backgroundColor: 'rgba(15, 23, 42, 0.6)',
    border: '1px solid rgba(59, 130, 246, 0.3)',
    borderRadius: '8px',
    outline: 'none',
    transition: 'all 0.3s ease',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  inputFocus: {
    borderColor: '#3b82f6',
    boxShadow: '0 0 0 3px rgba(59, 130, 246, 0.1)',
    backgroundColor: 'rgba(15, 23, 42, 0.8)',
  } as React.CSSProperties,

  inputHover: {
    borderColor: 'rgba(59, 130, 246, 0.5)',
    backgroundColor: 'rgba(15, 23, 42, 0.7)',
  } as React.CSSProperties,

  formOptions: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '24px',
  } as React.CSSProperties,

  checkbox: {
    color: '#ffffff',
  } as React.CSSProperties,

  forgotPassword: {
    color: '#3b82f6',
    textDecoration: 'none',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  loginButton: {
    width: '100%',
    height: '48px',
    backgroundColor: '#3b82f6',
    border: 'none',
    borderRadius: '8px',
    color: '#ffffff',
    fontSize: '16px',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  loginButtonHover: {
    backgroundColor: '#2563eb',
    transform: 'translateY(-1px)',
    boxShadow: '0 4px 12px rgba(59, 130, 246, 0.3)',
  } as React.CSSProperties,

  socialLogin: {
    marginTop: '32px',
    textAlign: 'center' as const,
  } as React.CSSProperties,

  divider: {
    position: 'relative' as const,
    marginBottom: '24px',
    textAlign: 'center' as const,
    color: 'rgba(255, 255, 255, 0.5)',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  wechatButton: {
    width: '100%',
    height: '48px',
    backgroundColor: 'rgba(30, 41, 59, 0.8)',
    border: '1px solid rgba(59, 130, 246, 0.3)',
    borderRadius: '8px',
    color: '#ffffff',
    fontSize: '16px',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  registerLink: {
    textAlign: 'center' as const,
    marginTop: '32px',
    color: 'rgba(255, 255, 255, 0.7)',
    fontSize: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,

  registerButton: {
    color: '#3b82f6',
    textDecoration: 'none',
    marginLeft: '8px',
    cursor: 'pointer',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  } as React.CSSProperties,
};

export const Login: React.FC<LoginProps> = ({ onSwitchToRegister, onLoginSuccess }) => {
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);
  const [inputStates, setInputStates] = useState({
    username: { focused: false, hovered: false },
    password: { focused: false, hovered: false },
  });
  const [buttonHovered, setButtonHovered] = useState(false);
  const { login } = useAuth();

  const handleSubmit = async (values: LoginFormData) => {
    setLoading(true);

    try {
      const success = await login(values.username, values.password);
      if (success) {
        onLoginSuccess?.();
      }
    } catch (error) {
      message.error('登录失败，请重试');
    } finally {
      setLoading(false);
    }
  };

  const handleWechatLogin = () => {
    message.info('微信登录功能开发中，敬请期待！');
  };

  const handleForgotPassword = () => {
    message.info('忘记密码功能开发中，敬请期待！');
  };

  const getInputStyle = (field: 'username' | 'password') => {
    const state = inputStates[field];
    return {
      ...styles.input,
      ...(state.focused ? styles.inputFocus : {}),
      ...(state.hovered && !state.focused ? styles.inputHover : {}),
    };
  };

  const handleInputFocus = (field: 'username' | 'password') => {
    setInputStates(prev => ({
      ...prev,
      [field]: { ...prev[field], focused: true }
    }));
  };

  const handleInputBlur = (field: 'username' | 'password') => {
    setInputStates(prev => ({
      ...prev,
      [field]: { ...prev[field], focused: false }
    }));
  };

  const handleInputMouseEnter = (field: 'username' | 'password') => {
    setInputStates(prev => ({
      ...prev,
      [field]: { ...prev[field], hovered: true }
    }));
  };

  const handleInputMouseLeave = (field: 'username' | 'password') => {
    setInputStates(prev => ({
      ...prev,
      [field]: { ...prev[field], hovered: false }
    }));
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <div style={styles.title}>欢迎回来</div>
        <div style={styles.subtitle}>登录您的账户以继续使用</div>
      </div>

      <Form
        form={form}
        name="login"
        onFinish={handleSubmit}
        layout="vertical"
        autoComplete="off"
      >
        <div style={styles.formGroup}>
          <label style={styles.label}>用户名</label>
          <Form.Item
            name="username"
            rules={[
              { required: true, message: '请输入用户名或邮箱' },
              { min: 3, message: '用户名至少需要3个字符' },
            ]}
            style={{ marginBottom: 0 }}
          >
            <Input
              placeholder="请输入用户名或邮箱"
              prefix={<UserOutlined style={{ color: 'rgba(255, 255, 255, 0.5)' }} />}
              style={getInputStyle('username')}
              onFocus={() => handleInputFocus('username')}
              onBlur={() => handleInputBlur('username')}
              onMouseEnter={() => handleInputMouseEnter('username')}
              onMouseLeave={() => handleInputMouseLeave('username')}
            />
          </Form.Item>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>密码</label>
          <Form.Item
            name="password"
            rules={[
              { required: true, message: '请输入密码' },
              { min: 6, message: '密码至少需要6个字符' },
            ]}
            style={{ marginBottom: 0 }}
          >
            <Input.Password
              placeholder="请输入密码"
              prefix={<LockOutlined style={{ color: 'rgba(255, 255, 255, 0.5)' }} />}
              style={getInputStyle('password')}
              onFocus={() => handleInputFocus('password')}
              onBlur={() => handleInputBlur('password')}
              onMouseEnter={() => handleInputMouseEnter('password')}
              onMouseLeave={() => handleInputMouseLeave('password')}
            />
          </Form.Item>
        </div>

        <div style={styles.formOptions}>
          <Form.Item name="remember" valuePropName="checked" style={{ marginBottom: 0 }}>
            <Checkbox style={styles.checkbox}>记住我</Checkbox>
          </Form.Item>
          <a style={styles.forgotPassword} onClick={handleForgotPassword}>
            忘记密码？
          </a>
        </div>

        <Form.Item style={{ marginBottom: 0 }}>
          <button
            type="submit"
            style={{
              ...styles.loginButton,
              ...(buttonHovered ? styles.loginButtonHover : {}),
            }}
            onMouseEnter={() => setButtonHovered(true)}
            onMouseLeave={() => setButtonHovered(false)}
            disabled={loading}
          >
            {loading ? '登录中...' : '登录'}
          </button>
        </Form.Item>
      </Form>

      <div style={styles.socialLogin}>
        <div style={styles.divider}>
          <span>或使用以下方式登录</span>
        </div>
        <button 
          style={styles.wechatButton}
          onClick={handleWechatLogin}
        >
          <WechatOutlined style={{ marginRight: '8px' }} />
          微信登录
        </button>
      </div>

      <div style={styles.registerLink}>
        还没有账号？
        <span style={styles.registerButton} onClick={onSwitchToRegister}>
          立即注册
        </span>
      </div>
    </div>
  );
};
