import React, { useState, useEffect } from 'react';
import { createApiUrl, API_ENDPOINTS } from '../config/api';

const MarketOverview = () => {
  const [overview, setOverview] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 获取市场概览数据
  const fetchOverview = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(createApiUrl(API_ENDPOINTS.MARKET_OVERVIEW));
      if (!response.ok) {
        throw new Error('获取市场概览失败');
      }

      const data = await response.json();
      setOverview(data.data);
    } catch (err) {
      console.error('获取市场概览错误:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOverview();
  }, []);

  // 自动刷新
  useEffect(() => {
    const interval = setInterval(fetchOverview, 300000); // 每5分钟刷新一次
    return () => clearInterval(interval);
  }, []);

  const formatNumber = (num) => {
    if (num === null || num === undefined) return '--';
    return num.toLocaleString();
  };

  const formatPrice = (price) => {
    if (price === null || price === undefined) return '--';
    return price.toFixed(2);
  };

  const formatChange = (change) => {
    if (change === null || change === undefined) return '--';
    const prefix = change >= 0 ? '+' : '';
    return `${prefix}${change.toFixed(2)}%`;
  };

  const getChangeClass = (change) => {
    if (change > 0) return 'price-up';
    if (change < 0) return 'price-down';
    return 'price-neutral';
  };

  if (loading && !overview) {
    return (
      <div className="card">
        <h2 className="section-title">🏛️ 市场概览</h2>
        <div className="loading">
          <p>加载市场数据中...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="card">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2 className="section-title" style={{ marginBottom: 0 }}>🏛️ 市场概览</h2>
        <button className="btn-small btn" onClick={fetchOverview}>
          🔄 刷新
        </button>
      </div>

      {error ? (
        <div className="error">
          {error}
          <button 
            className="btn-small btn" 
            onClick={fetchOverview}
            style={{ marginLeft: '10px' }}
          >
            重试
          </button>
        </div>
      ) : overview ? (
        <div>
          {/* 主要指数 */}
          {overview.indices && overview.indices.length > 0 && (
            <div>
              <h3 style={{ fontSize: '1.1rem', marginBottom: '15px', color: '#333' }}>主要指数</h3>
              <div className="stats-grid">
                {overview.indices.map((index, i) => (
                  <div key={i} className="stat-item">
                    <div className="stat-value" style={{ fontSize: '1.5rem' }}>
                      {formatPrice(index.current_price)}
                    </div>
                    <div className="stat-label" style={{ marginBottom: '5px' }}>
                      {index.name}
                    </div>
                    <div className={`price-change ${getChangeClass(index.change_percent)}`}>
                      {formatChange(index.change_percent)}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* 市场统计 */}
          {overview.market_stats && (
            <div style={{ marginTop: '25px' }}>
              <h3 style={{ fontSize: '1.1rem', marginBottom: '15px', color: '#333' }}>市场统计</h3>
              <div className="stats-grid">
                <div className="stat-item">
                  <div className="stat-value" style={{ color: '#4caf50' }}>
                    {formatNumber(overview.market_stats.rising_stocks)}
                  </div>
                  <div className="stat-label">上涨股票</div>
                </div>
                <div className="stat-item">
                  <div className="stat-value" style={{ color: '#f44336' }}>
                    {formatNumber(overview.market_stats.falling_stocks)}
                  </div>
                  <div className="stat-label">下跌股票</div>
                </div>
                <div className="stat-item">
                  <div className="stat-value" style={{ color: '#666' }}>
                    {formatNumber(overview.market_stats.unchanged_stocks)}
                  </div>
                  <div className="stat-label">平盘股票</div>
                </div>
              </div>
            </div>
          )}

          {/* 热门股票 */}
          {overview.hot_stocks && overview.hot_stocks.length > 0 && (
            <div style={{ marginTop: '25px' }}>
              <h3 style={{ fontSize: '1.1rem', marginBottom: '15px', color: '#333' }}>热门股票</h3>
              <div style={{ maxHeight: '200px', overflowY: 'auto' }}>
                {overview.hot_stocks.map((stock, i) => (
                  <div key={i} className="stock-item" style={{ marginBottom: '8px', padding: '10px' }}>
                    <div className="stock-info">
                      <div className="stock-code" style={{ fontSize: '0.9rem' }}>{stock.code}</div>
                      <div className="stock-name" style={{ fontSize: '0.8rem' }}>{stock.name}</div>
                    </div>
                    <div className="stock-price">
                      <div className="current-price" style={{ fontSize: '1rem' }}>
                        ¥{formatPrice(stock.current_price)}
                      </div>
                      <div className={`price-change ${getChangeClass(stock.change_percent)}`} style={{ fontSize: '0.8rem' }}>
                        {formatChange(stock.change_percent)}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          <div style={{ marginTop: '15px', padding: '10px', background: '#f9f9f9', borderRadius: '4px', fontSize: '0.9rem', color: '#666' }}>
            📊 市场数据每5分钟自动更新 | 最后更新: {new Date().toLocaleTimeString()}
          </div>
        </div>
      ) : (
        <div className="empty-state">
          <div className="empty-state-icon">📊</div>
          <p>暂无市场数据</p>
        </div>
      )}
    </div>
  );
};

export default MarketOverview;
