 */

import React, { useState } from 'react';
import { authService } from '../services/authService';

interface LoginPageProps {
  setIsLoggedIn: (isLoggedIn: boolean) => void;
}

const LoginPage: React.FC<LoginPageProps> = ({ setIsLoggedIn }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  // Â§ÑÁêÜÁôªÂΩï
  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!username.trim()) {
      alert('ËØ∑ËæìÂÖ•Áî®Êà∑Âêç');
      return;
    }

    if (!password.trim()) {
      alert('ËØ∑ËæìÂÖ•ÂØÜÁ†Å');
      return;
    }

    setLoading(true);
    console.log('ÂºÄÂßãÁôªÂΩïÔºåÁî®Êà∑Âêç:', username);

    try {
      const response = await authService.login({ username, password });
      console.log('ÁôªÂΩïÂìçÂ∫î:', response);

      if (response.success && response.data?.token) {
        // ‰øùÂ≠òtoken
        localStorage.setItem('userToken', response.data.token);
        console.log('Â∑≤‰øùÂ≠òtokenÂà∞localStorage');

        // Êõ¥Êñ∞ÁôªÂΩïÁä∂ÊÄÅ
        setIsLoggedIn(true);
        console.log('Â∑≤Êõ¥Êñ∞ÁôªÂΩïÁä∂ÊÄÅ‰∏∫true');

        alert('ÁôªÂΩïÊàêÂäüÔºÅ');
      } else {
        console.error('ÁôªÂΩïÂ§±Ë¥•:', response.message);
        alert(response.message || 'ÁôªÂΩïÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
      }
    } catch (error: any) {
      console.error('ÁôªÂΩïÂ§±Ë¥•:', error);
      alert(error.message || 'ÁôªÂΩïÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
    } finally {
      setLoading(false);
    }
  };

  // Â§ÑÁêÜËÆ∞‰ΩèÊàëÂ§çÈÄâÊ°ÜÁÇπÂáª
  const handleRememberMeClick = () => {
    setRememberMe(!rememberMe);
  };

  // Â§ÑÁêÜÂøòËÆ∞ÂØÜÁ†ÅÁÇπÂáª
  const handleForgotPasswordClick = (e: React.MouseEvent) => {
    e.preventDefault();
    alert('ÂøòËÆ∞ÂØÜÁ†ÅÂäüËÉΩÂºÄÂèë‰∏≠ÔºåËØ∑ËÅîÁ≥ªÁÆ°ÁêÜÂëòÔºÅ');
  };

  // Â§ÑÁêÜÁ§æ‰∫§ÁôªÂΩïÊåâÈíÆÁÇπÂáª
  const handleSocialLoginClick = (e: React.MouseEvent, platform: string) => {
    e.preventDefault();
    alert(`${platform}ÂäüËÉΩÂºÄÂèë‰∏≠ÔºåÊï¨ËØ∑ÊúüÂæÖÔºÅ`);
  };

  return (
    <>
      <div className="login-header">
        <div className="login-title">Ê¨¢ËøéÂõûÊù•</div>
        <div className="login-subtitle">ÁôªÂΩïÊÇ®ÁöÑË¥¶Êà∑‰ª•ÁªßÁª≠‰ΩøÁî®</div>
      </div>

      <form id="loginForm" onSubmit={handleLogin}>
        <div className="form-group">
          <label className="form-label">Áî®Êà∑Âêç</label>
          <div className="input-wrapper">
            <input 
              type="text" 
              className="form-input" 
              placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑Âêç" 
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              autoComplete="new-password"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.06)' }}
            />
            <div className="input-icon">üë§</div>
          </div>
        </div>

        <div className="form-group">
          <label className="form-label">ÂØÜÁ†Å</label>
          <div className="input-wrapper">
            <input 
              type={showPassword ? "text" : "password"} 
              className="form-input" 
              placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              autoComplete="new-password"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.06)' }}
            />
            <div 
              className="input-icon password-toggle" 
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? 'üëÅÔ∏è' : 'üîí'}
            </div>
          </div>
        </div>

        <div className="form-options">
          <label className="remember-me" onClick={handleRememberMeClick}>
            <div className={`custom-checkbox ${rememberMe ? 'checked' : ''}`}></div>
            ËÆ∞‰ΩèÊàë
          </label>
          <button type="button" className="forgot-password" onClick={handleForgotPasswordClick}>ÂøòËÆ∞ÂØÜÁ†ÅÔºü</button>
        </div>

        <button type="submit" className="login-button" disabled={loading}>
          {loading ? (
            <>
              <div className="loading"></div>
              <span>ÁôªÂΩï‰∏≠...</span>
            </>
          ) : (
            <span className="button-text">ÁôªÂΩï</span>
          )}
        </button>
      </form>

      <div className="social-login">
        <div className="divider">
          <span>Êàñ‰ΩøÁî®‰ª•‰∏ãÊñπÂºèÁôªÂΩï</span>
        </div>
        <div className="social-buttons">
          <button 
            type="button" 
            className="social-button"
            onClick={(e) => handleSocialLoginClick(e, 'ÂæÆ‰ø°ÁôªÂΩï')}
          >
            <span>üí¨</span>
            ÂæÆ‰ø°ÁôªÂΩï
          </button>
          <button 
            type="button" 
            className="social-button"
            onClick={(e) => handleSocialLoginClick(e, 'ÊîØ‰ªòÂÆùÁôªÂΩï')}
          >
            <span>üì±</span>
            ÊîØ‰ªòÂÆùÁôªÂΩï
          </button>
        </div>
      </div>

      <div className="register-link">
        ËøòÊ≤°ÊúâË¥¶Âè∑Ôºü<button type="button" className="register-button">Á´ãÂç≥Ê≥®ÂÜå</button>
      </div>
    </>
  );
};

export default LoginPage;
