/**
 * 登录组件
 * 提供用户登录界面
 */

import React, { useState } from 'react';
import { Form, Input, Button, Checkbox, message } from 'antd';
import { UserOutlined, LockOutlined, WechatOutlined } from '@ant-design/icons';
import { useAuth } from '../../contexts/AuthContext';
import './Auth.css';

interface LoginFormData {
  username: string;
  password: string;
  remember?: boolean;
}

interface LoginProps {
  onSwitchToRegister: () => void;
  onLoginSuccess?: () => void;
}

export const Login: React.FC<LoginProps> = ({ onSwitchToRegister, onLoginSuccess }) => {
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();

  const handleSubmit = async (values: LoginFormData) => {
    setLoading(true);

    try {
      const success = await login(values.username, values.password);
      if (success) {
        onLoginSuccess?.();
      }
    } catch (error) {
      message.error('登录失败，请重试');
    } finally {
      setLoading(false);
    }
  };

  const handleWechatLogin = () => {
    message.info('微信登录功能开发中，敬请期待！');
  };

  const handleForgotPassword = () => {
    message.info('忘记密码功能开发中，敬请期待！');
  };

  return (
    <div className="modern-auth-container">
      {/* 动态背景 */}
      <div className="grid-background"></div>
      <div className="gradient-overlay"></div>
      <div className="floating-shapes">
        <div className="shape"></div>
        <div className="shape"></div>
        <div className="shape"></div>
        <div className="shape"></div>
      </div>

      {/* 主容器 */}
      <div className="main-container">
        {/* 左侧内容 */}
        <div className="left-content">
          <div className="brand-section">
            <div className="brand-logo">
              <div className="logo-icon">📊</div>
              <div className="brand-title">StockInsight</div>
            </div>
            <div className="brand-subtitle">
